// src/redux/slices/bookingSlice.js

import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// ðŸ” Ð‘ÐµÐ·Ð¾Ð¿Ð°ÑÐ½Ð¾Ðµ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ VITE_API_URL
const getApiUrl = () => {
  if (typeof import.meta !== 'undefined' && import.meta.env) {
    return import.meta.env.VITE_API_URL || 'http://127.0.0.1:8000';
  }
  return 'http://127.0.0.1:8000';
};

const API_BASE_URL = getApiUrl();

// ðŸ“¤ Ð¡Ð¾Ð·Ð´Ð°Ð½Ð¸Ðµ Ð·Ð°ÑÐ²ÐºÐ¸
export const createBooking = createAsyncThunk(
  'booking/createBooking',
  async (bookingData, { rejectWithValue }) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/api/bookings/`, bookingData);
      return response.data;
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐµ Ð·Ð°ÑÐ²ÐºÐ¸:', error.response?.data || error.message);
      return rejectWithValue(
        error.response?.data?.detail || 
        error.message || 
        'ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ Ð·Ð°ÑÐ²ÐºÑƒ'
      );
    }
  }
);

// ðŸ“¥ ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ Ð²ÑÐµÑ… Ð·Ð°ÑÐ²Ð¾Ðº (Ð´Ð»Ñ Ð°Ð´Ð¼Ð¸Ð½ÐºÐ¸)
export const getBookings = createAsyncThunk(
  'booking/getBookings',
  async (_, { rejectWithValue }) => {
    try {
      const token = localStorage.getItem('access_token');
      const headers = token ? { Authorization: `Bearer ${token}` } : {};
      
      const response = await axios.get(`${API_BASE_URL}/api/admin/bookings/`, { headers });
      return response.data;
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ Ð·Ð°ÑÐ²Ð¾Ðº:', error.response?.data || error.message);
      return rejectWithValue(
        error.response?.data?.detail || 
        error.message || 
        'ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð·Ð°Ð³Ñ€ÑƒÐ·Ð¸Ñ‚ÑŒ Ð·Ð°ÑÐ²ÐºÐ¸'
      );
    }
  }
);

// âœ… ÐžÑ‚Ð¼ÐµÑ‚ÐºÐ° Ð·Ð°ÑÐ²ÐºÐ¸ ÐºÐ°Ðº Ð¿Ñ€Ð¾Ñ‡Ð¸Ñ‚Ð°Ð½Ð½Ð¾Ð¹
export const markBookingAsRead = createAsyncThunk(
  'booking/markBookingAsRead',
  async (bookingId, { rejectWithValue }) => {
    try {
      const token = localStorage.getItem('access_token');
      const headers = token ? { Authorization: `Bearer ${token}` } : {};
      
      const response = await axios.put(
        `${API_BASE_URL}/api/admin/bookings/${bookingId}/mark-read`,
        {},
        { headers }
      );
      return response.data;
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¾Ñ‚Ð¼ÐµÑ‚ÐºÐµ Ð·Ð°ÑÐ²ÐºÐ¸:', error.response?.data || error.message);
      return rejectWithValue(
        error.response?.data?.detail || 
        error.message || 
        'ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð¾Ñ‚Ð¼ÐµÑ‚Ð¸Ñ‚ÑŒ Ð·Ð°ÑÐ²ÐºÑƒ'
      );
    }
  }
);

const bookingSlice = createSlice({
  name: 'booking',
  initialState: {
    // Ð”Ð»Ñ Ñ„Ð¾Ñ€Ð¼Ñ‹ ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ñ
    loading: false,
    error: null,
    success: false,
    
    // Ð”Ð»Ñ ÑÐ¿Ð¸ÑÐºÐ° Ð·Ð°ÑÐ²Ð¾Ðº
    bookings: [],
    bookingsLoading: false,
    bookingsError: null,
  },
  reducers: {
    resetStatus: (state) => {
      state.loading = false;
      state.success = false;
      state.error = null;
    },
  },
  extraReducers: (builder) => {
    // ðŸ“¤ Ð¡Ð¾Ð·Ð´Ð°Ð½Ð¸Ðµ Ð·Ð°ÑÐ²ÐºÐ¸
    builder
      .addCase(createBooking.pending, (state) => {
        state.loading = true;
        state.error = null;
        state.success = false;
      })
      .addCase(createBooking.fulfilled, (state) => {
        state.loading = false;
        state.success = true;
      })
      .addCase(createBooking.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      
      // ðŸ“¥ ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ ÑÐ¿Ð¸ÑÐºÐ° Ð·Ð°ÑÐ²Ð¾Ðº
      .addCase(getBookings.pending, (state) => {
        state.bookingsLoading = true;
        state.bookingsError = null;
      })
      .addCase(getBookings.fulfilled, (state, action) => {
        state.bookingsLoading = false;
        state.bookings = action.payload;
      })
      .addCase(getBookings.rejected, (state, action) => {
        state.bookingsLoading = false;
        state.bookingsError = action.payload;
      })
      
      // âœ… ÐžÑ‚Ð¼ÐµÑ‚ÐºÐ° ÐºÐ°Ðº Ð¿Ñ€Ð¾Ñ‡Ð¸Ñ‚Ð°Ð½Ð½Ð°Ñ
      .addCase(markBookingAsRead.fulfilled, (state, action) => {
        // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÑ‚Ð°Ñ‚ÑƒÑ ÐºÐ¾Ð½ÐºÑ€ÐµÑ‚Ð½Ð¾Ð¹ Ð·Ð°ÑÐ²ÐºÐ¸ Ð² ÑÐ¿Ð¸ÑÐºÐµ
        const index = state.bookings.findIndex(b => b.booking_id === action.payload.booking_id);
        if (index !== -1) {
          state.bookings[index] = action.payload;
        }
      });
  },
});

export const { resetStatus } = bookingSlice.actions;
export default bookingSlice.reducer;
