// redux/slices/apiSlice.js
import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

export const apiSlice = createApi({
  reducerPath: 'api',
  baseQuery: fetchBaseQuery({ baseUrl: 'http://127.0.0.1:8000/api' }),
  tagTypes: ['Baths', 'Bookings'],
  endpoints: (builder) => ({
    // --- Получение всех бань ---
    getBaths: builder.query({
      query: () => '/baths/',
      transformResponse: (response) => {
        // Защита: если ответ не массив — вернуть пустой массив
        if (!Array.isArray(response)) {
          console.error('Expected array, got:', response);
          return [];
        }

        return response.map(bath => {
          // Явно извлекаем bath_id
          const bathId = bath.bath_id;

          // Если bath_id нет — логируем и пропускаем
          if (!bathId) {
            console.warn('Bath without bath_id:', bath);
            return null;
          }

          return {
            ...bath,
            bath_id: bathId, // гарантируем, что есть
            image: bath.photos?.[0]?.image_url || '/img/placeholder.jpg',
            path: `/baths/${bathId}`,
            subtitle: bath.title,
          };
        }).filter(Boolean); // убираем null
      },
      providesTags: ['Baths'],
    }),

    // --- Получение одной бани ---
    getBathById: builder.query({
      query: (id) => `/baths/${id}`,
      transformResponse: (response) => {
        if (!response) return null;

        return {
          ...response,
          bath_id: response.bath_id,
          image: response.photos?.[0]?.image_url || '/img/placeholder.jpg',
          images: response.photos?.map(p => p.image_url) || [],
          path: `/baths/${response.bath_id}`,
          subtitle: response.title,
        };
      },
      providesTags: ['Bath'],
    }),

    // --- Создание брони ---
    createBooking: builder.mutation({
      query: (bookingData) => ({
        url: '/bookings/',
        method: 'POST',
        body: bookingData,
      }),
      invalidatesTags: ['Bookings'],
    }),
  }),
});

export const { 
  useGetBathsQuery, 
  useGetBathByIdQuery, 
  useCreateBookingMutation 
} = apiSlice;
