// src/pages/Admin/Reservations/BookingDetailsModal.jsx

import { useState } from 'react';
import BookingViewModal from './BookingViewModal';
import BookingEditModal from './BookingEditModal';

function BookingDetailsModal({ booking, onClose, onEdit, onDelete }) {
  const [isEditing, setIsEditing] = useState(false);

  if (!booking) return null;

  if (isEditing) {
    return (
      <BookingEditModal
        booking={booking}
        onClose={onClose}
        onEdit={onEdit}
        onSwitchToView={() => setIsEditing(false)}
      />
    );
  }

  return (
    <BookingViewModal
      booking={booking}
      onClose={onClose}
      onEdit={onEdit}
      onDelete={onDelete}
      onSwitchToEdit={() => setIsEditing(true)}
    />
  );
}

export default BookingDetailsModal;
