import { useState, useEffect } from 'react';
import { useGetBathsQuery, useCreateBookingMutation } from '../../redux/slices/apiSlice';
import CustomButton from '../../components/UI/CustomButton/CustomButton';

function Booking() {
  // Загружаем список бань
  const {  baths = [], isLoading: isLoadingBaths, error: bathError } = useGetBathsQuery();

  // Мутация для создания брони
  const [createBooking, { isLoading: isSubmitting }] = useCreateBookingMutation();

  // Состояние формы
  const [formData, setFormData] = useState({
    date: '',
    hours: 3,
    bath_id: '',
    guests: 1,
    name: '',
    phone: '',
    email: '',
    notes: '',
  });

  // Сообщения
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  // Обработчик изменения полей
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Обработчик отправки формы
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Сброс предыдущих сообщений
    setSuccess('');
    setError('');

    // Валидация обязательных полей
    if (!formData.bath_id || !formData.date || !formData.name || !formData.phone) {
      setError('Пожалуйста, заполните все обязательные поля.');
      return;
    }

    // Подготовка данных для отправки
    const bookingData = {
      bath_id: Number(formData.bath_id),
      date: formData.date,
      duration_hours: Number(formData.hours),
      guests: Number(formData.guests),
      name: formData.name.trim(),
      phone: formData.phone.trim(),
      email: formData.email?.trim() || null,
      notes: formData.notes.trim() || null,
    };

    try {
      // Отправка на бэкенд
      await createBooking(bookingData).unwrap();

      // Успех
      setSuccess('Ваша заявка успешно отправлена! Мы свяжемся с вами в ближайшее время.');
      setError('');

      // Очистка формы
      setFormData({
        date: '',
        hours: 3,
        bath_id: '',
        guests: 1,
        name: '',
        phone: '',
        email: '',
        notes: '',
      });

      // Авто-скрытие сообщения об успехе
      setTimeout(() => {
        setSuccess('');
      }, 5000);
    } catch (err) {
      const errorMsg = err.data?.detail || 'Не удалось отправить заявку. Попробуйте позже.';
      setError(errorMsg);
      console.error('Ошибка отправки бронирования:', err);
    }
  };

  // Логирование для отладки
  useEffect(() => {
    console.log('Загруженные бани:', baths);
    console.log('Ошибка загрузки:', bathError);
  }, [baths, bathError]);


  

  // Пока грузятся бани
  if (isLoadingBaths) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <p className="text-xl text-gray-600">Загрузка бань...</p>
      </div>
    );
  }

  // Ошибка загрузки бань
  if (bathError) {
    return (
      <div className="text-center py-20">
        <p className="text-2xl text-red-600">Ошибка загрузки бань</p>
        <p className="text-gray-500 mt-2">Не удалось подключиться к серверу</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-amber-50 via-white to-green-50 py-12 px-4 sm:px-6 lg:px-8 mt-14">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-5">
          <h1 className="text-4xl sm:text-5xl font-light text-gray-800 mb-4">Забронировать баню</h1>
          <p className="text-lg text-gray-600 font-extralight max-w-2xl mx-auto">
            Выберите удобную дату и время — мы подготовим парную к вашему приходу.
            Уют, чистота и ароматные веники уже ждут вас!
          </p>
        </div>

        <div className="bg-white rounded-3xl shadow-xl p-8 md:p-10 border border-gray-100">
          <form onSubmit={handleSubmit} className="space-y-8">
            {/* Дата и часы */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-2">
                  Дата посещения
                </label>
                <input
                  type="date"
                  id="date"
                  name="date"
                  value={formData.date}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  required
                />
              </div>
              <div>
                <label htmlFor="hours" className="block text-sm font-medium text-gray-700 mb-2">
                  Продолжительность (в часах)
                </label>
                <input
                  type="number"
                  id="hours"
                  name="hours"
                  min="1"
                  max="12"
                  value={formData.hours}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  required
                />
              </div>
            </div>

            {/* Выбор бани */}
            <div>
              <label htmlFor="bath_id" className="block text-sm font-medium text-gray-700 mb-2">
                Выберите баню
              </label>
              <select
                id="bath_id"
                name="bath_id"
                value={formData.bath_id}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent bg-white"
                required
              >
                <option value="">Выберите баню</option>
                {baths.length === 0 ? (
  <option disabled>Нет доступных бань</option>
) : (
  baths.map((bath) => (
    <option key={bath.bath_id} value={bath.bath_id}>
      {bath.name}
    </option>
  ))
)}
              </select>
            </div>

            {/* Гости */}
            <div>
              <label htmlFor="guests" className="block text-sm font-medium text-gray-700 mb-2">
                Количество гостей
              </label>
              <input
                type="number"
                id="guests"
                name="guests"
                min="1"
                max="12"
                value={formData.guests}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                required
              />
            </div>

            {/* Имя и телефон */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
                  Ваше имя
                </label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="Иван"
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  required
                />
              </div>
              <div>
                <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-2">
                  Телефон
                </label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  placeholder="+7 (999) 123-45-67"
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  required
                />
              </div>
            </div>

            {/* Дополнительно */}
            <div>
              <label htmlFor="notes" className="block text-sm font-medium text-gray-700 mb-2">
                Дополнительно (веники, шашлык, аренда посуды и т.д.)
              </label>
              <textarea
                id="notes"
                name="notes"
                rows="3"
                value={formData.notes}
                onChange={handleChange}
                placeholder="Хотим кедровые веники и мангал на 5 часов..."
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent resize-none"
              ></textarea>
            </div>

            {/* Кнопка и сообщения */}
            <div className="pt-4 space-y-3">
              <div className="text-center">
                <CustomButton
                  type="submit"
                  text={isSubmitting ? 'Отправка...' : 'Забронировать'}
                  variant="green"
                  className="px-10 py-3 text-lg font-medium hover:shadow-xl transition-all duration-300"
                  disabled={isSubmitting}
                />
              </div>

              {success && (
                <div className="p-4 bg-green-100 border border-green-200 text-green-800 text-sm rounded-xl text-center">
                  {success}
                </div>
              )}

              {error && (
                <div className="p-4 bg-red-100 border border-red-200 text-red-800 text-sm rounded-xl text-center">
                  {error}
                </div>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Booking;
