// src/redux/slices/apiSlice.js
import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

export const apiSlice = createApi({
  reducerPath: 'api', // üëà –û—Å—Ç–∞–≤–ª—è–µ–º –∫–∞–∫ –µ—Å—Ç—å ‚Äî –Ω–µ —Ç—Ä–æ–≥–∞–µ–º!
  baseQuery: fetchBaseQuery({
    baseUrl: 'http://127.0.0.1:8000/api',
    prepareHeaders: (headers) => {
      return headers;
    },
  }),
  tagTypes: ['Baths', 'Bookings', 'Massages'], // üëà –î–û–ë–ê–í–ò–õ–ò Massages

  endpoints: (builder) => ({
    // ========================
    // üõÅ –ë–ê–ù–ò (—É–∂–µ —Ä–∞–±–æ—Ç–∞—é—Ç)
    // ========================
    getBaths: builder.query({
      query: () => '/baths/',
      transformResponse: (response) => {
        if (!Array.isArray(response)) {
          console.error('Expected array, got:', response);
          return [];
        }

        return response.map(bath => {
          const bathId = bath.bath_id;
          if (!bathId) {
            console.warn('Bath without bath_id:', bath);
            return null;
          }

          return {
            ...bath,
            bath_id: bathId,
            image: bath.photos?.[0]?.image_url || '/img/placeholder.jpg',
            path: `/baths/${bathId}`,
            subtitle: bath.title,
          };
        }).filter(Boolean);
      },
      providesTags: [{ type: 'Baths', id: 'LIST' }],
    }),

    getBathById: builder.query({
      query: (id) => `/baths/${id}`,
      transformResponse: (response) => {
        if (!response) return null;

        return {
          ...response,
          bath_id: response.bath_id,
          image: response.photos?.[0]?.image_url || '/img/placeholder.jpg',
          images: response.photos?.map(p => p.image_url) || [],
          path: `/baths/${response.bath_id}`,
          subtitle: response.title,
        };
      },
      providesTags: (result, error, arg) =>
        result
          ? [{ type: 'Baths', id: arg }]
          : [{ type: 'Baths', id: 'LIST' }],
    }),

    // ========================
    // üíÜ –ú–ê–°–°–ê–ñ–ò (–ù–û–í–´–ï!)
    // ========================
    getMassages: builder.query({
      query: () => '/massages',
      transformResponse: (response) => {
        if (!Array.isArray(response)) {
          console.error('Expected array for massages, got:', response);
          return [];
        }

        return response.map(massage => {
          const massageId = massage.massage_id;
          if (!massageId) {
            console.warn('Massage without massage_id:', massage);
            return null;
          }

          return {
            ...massage,
            massage_id: massageId,
            price: `${massage.cost} ‚ÇΩ`, // –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º —Ü–µ–Ω—É —Å—Ä–∞–∑—É –∑–¥–µ—Å—å
            path: `/booking`, // –ü–æ–∫–∞ –æ—Å—Ç–∞–≤–ª—è–µ–º –æ–¥–∏–Ω –ø—É—Ç—å ‚Äî –º–æ–∂–Ω–æ —Å–¥–µ–ª–∞—Ç—å –æ—Ç–¥–µ–ª—å–Ω—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É –º–∞—Å—Å–∞–∂–∞ –ø–æ–∑–∂–µ
          };
        }).filter(Boolean);
      },
      providesTags: [{ type: 'Massages', id: 'LIST' }],
    }),

    getMassageById: builder.query({
      query: (id) => `/massages/${id}`,
      transformResponse: (response) => {
        if (!response) return null;

        return {
          ...response,
          massage_id: response.massage_id,
          price: `${response.cost} ‚ÇΩ`,
          path: `/booking`,
        };
      },
      providesTags: (result, error, arg) =>
        result
          ? [{ type: 'Massages', id: arg }]
          : [{ type: 'Massages', id: 'LIST' }],
    }),
  }),
});

// –≠–∫—Å–ø–æ—Ä—Ç–∏—Ä—É–µ–º —Ö—É–∫–∏
export const {
  useGetBathsQuery,
  useGetBathByIdQuery,
  useGetMassagesQuery,     // üëà –ù–û–í–´–ô –•–£–ö
  useGetMassageByIdQuery,  // üëà –ù–û–í–´–ô –•–£–ö
} = apiSlice;
