// src/pages/Admin/AdminReservations/AdminReservations.jsx

import { useState } from 'react';
import ReservationsFilters from '../Reservations/ReservationsFilters';
import ReservationsTable from '../Reservations/ReservationsTable';
import BookingDetailsModal from '../Reservations/BookingDetailsModal';

const mockReservations = [
  {
    reservation_id: 1,
    bath_id: 1,
    start_datetime: "2025-04-05T10:00:00",
    end_datetime: "2025-04-05T12:00:00",
    client_name: "Иван Иванов",
    client_phone: "+7 (999) 123-45-67",
    client_email: "ivan@example.com",
    notes: "Хочу веники",
    total_cost: 6000,
    services: [
      { service_id: 1, name: "Веники", cost: 500, quantity: 2 }
    ]
  },
  {
    reservation_id: 2,
    bath_id: 1,
    start_datetime: "2025-04-05T14:00:00",
    end_datetime: "2025-04-05T15:30:00",
    client_name: "Мария Петрова",
    client_phone: "+7 (987) 654-32-10",
    client_email: "maria@example.com",
    notes: "",
    total_cost: 4500,
    services: []
  }
];

function AdminReservations() {
  const [reservations, setReservations] = useState([]);
  const [filters, setFilters] = useState(null);
  const [selectedBooking, setSelectedBooking] = useState(null);

  const handleApply = (filters) => {
    if (filters.bathId === '1' && filters.date === '2025-04-05') {
      setReservations(mockReservations);
    } else {
      setReservations([]);
    }
    setFilters(filters);
  };

  const handleViewBooking = (booking) => {
    setSelectedBooking(booking);
  };

  const handleEditBooking = (booking) => {
    console.log('Редактировать:', booking);
    setSelectedBooking(null);
    // Здесь можно открыть модалку редактирования
  };

  const handleDeleteBooking = (id) => {
    if (window.confirm('Вы уверены, что хотите удалить эту бронь?')) {
      setReservations(prev => prev.filter(b => b.reservation_id !== id));
      setSelectedBooking(null);
    }
  };

  const handleCloseModal = () => {
    setSelectedBooking(null);
  };

  return (
    <div>
      <h2 className="text-2xl font-semibold text-gray-800 mb-6">Записи</h2>

      <ReservationsFilters onApply={handleApply} />

      <ReservationsTable
        reservations={reservations}
        date={filters?.date}
        bathId={filters?.bathId}
        onViewBooking={handleViewBooking}
      />

      <BookingDetailsModal
        booking={selectedBooking}
        onClose={handleCloseModal}
        onEdit={handleEditBooking}
        onDelete={handleDeleteBooking}
      />
    </div>
  );
}

export default AdminReservations;
