import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createBooking, resetStatus } from '../../redux/slices/bookingSlice';

function Booking() {
  const dispatch = useDispatch();

  // Состояние формы
  const [formData, setFormData] = useState({
    date: '',
    duration_hours: 1,
    bath_id: '',
    guests: 1,
    name: '',
    phone: '',
    email: '',
    notes: '',
  });

  // Состояние ошибок валидации
  const [errors, setErrors] = useState({});

  // Состояние из Redux
  const { loading, success, error: submitError } = useSelector((state) => state.booking);

  // Обработчик изменения полей
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    // Убираем ошибку при вводе
    if (errors[name]) {
      setErrors((prev) => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  // Валидация обязательных полей
  const validate = () => {
    const newErrors = {};

    if (!formData.date) newErrors.date = 'Укажите дату';
    if (!formData.bath_id) newErrors.bath_id = 'Выберите баню';
    if (!formData.guests || formData.guests < 1) newErrors.guests = 'Укажите количество гостей';
    if (!formData.name?.trim()) newErrors.name = 'Введите имя';
    if (!formData.phone?.trim()) newErrors.phone = 'Введите телефон';

    // Email — только если заполнен, но с ошибкой
    if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Некорректный email';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Обработчик отправки
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validate()) return;

    dispatch(createBooking(formData))
      .unwrap()
      .then(() => {
        // Успех: сбрасываем форму и статус
        setFormData({
          date: '',
          duration_hours: 1,
          bath_id: '',
          guests: 1,
          name: '',
          phone: '',
          email: '',
          notes: '',
        });
        setTimeout(() => {
          dispatch(resetStatus());
        }, 3000);
      })
      .catch(() => {
        // Ошибка остаётся в state.booking.error, показываем её
      });
  };

  // Сброс состояния после закрытия сообщения
  const clearMessage = () => {
    dispatch(resetStatus());
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-amber-50 via-white to-green-50 py-12 px-4 sm:px-6 lg:px-8 mt-14">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-5">
          <h1 className="text-4xl sm:text-5xl font-light text-gray-800 mb-4">Забронировать баню</h1>
          <p className="text-lg text-gray-600 font-extralight max-w-2xl mx-auto">
            Выберите удобную дату и время — мы подготовим парную к вашему приходу.
            Уют, чистота и ароматные веники уже ждут вас!
          </p>
        </div>

        <div className="bg-white rounded-3xl shadow-xl p-8 md:p-10 border border-gray-100">
          <form onSubmit={handleSubmit} className="space-y-8">
            {/* Сообщение об успехе или ошибке */}
            {success && (
              <div className="text-center text-green-600 font-medium bg-green-50 py-3 rounded-xl animate-fade-in">
                ✅ Бронь успешно отправлена! Мы свяжемся с вами.
                <button
                  type="button"
                  onClick={clearMessage}
                  className="block mx-auto mt-1 text-sm underline hover:text-green-700"
                >
                  Закрыть
                </button>
              </div>
            )}
            {submitError && !success && (
              <div className="text-center text-red-600 font-medium bg-red-50 py-3 rounded-xl animate-fade-in">
                ❌ Ошибка: {submitError}
                <button
                  type="button"
                  onClick={clearMessage}
                  className="block mx-auto mt-1 text-sm underline hover:text-red-700"
                >
                  Закрыть
                </button>
              </div>
            )}

            {/* Дата и часы */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-2">
                  Дата посещения
                </label>
                <input
                  type="date"
                  id="date"
                  name="date"
                  value={formData.date}
                  onChange={handleChange}
                  className={`w-full px-4 py-3 border rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent ${
                    errors.date ? 'border-red-500' : 'border-gray-300'
                  }`}
                />
                {errors.date && <p className="text-red-500 text-sm mt-1">{errors.date}</p>}
              </div>
              <div>
                <label htmlFor="duration_hours" className="block text-sm font-medium text-gray-700 mb-2">
                  Продолжительность (в часах)
                </label>
                <input
                  type="number"
                  id="duration_hours"
                  name="duration_hours"
                  min="1"
                  max="12"
                  value={formData.duration_hours}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                />
              </div>
            </div>

            {/* Выбор бани */}
            <div>
              <label htmlFor="bath_id" className="block text-sm font-medium text-gray-700 mb-2">
                Выберите баню
              </label>
              <select
                id="bath_id"
                name="bath_id"
                value={formData.bath_id}
                onChange={handleChange}
                className={`w-full px-4 py-3 border rounded-xl bg-white ${
                  errors.bath_id ? 'border-red-500' : 'border-gray-300'
                }`}
              >
                <option value="">Выберите баню</option>
                <option value="1">Кедровая баня</option>
                <option value="2">Нефритовая баня</option>
                <option value="3">Липовая баня</option>
              </select>
              {errors.bath_id && <p className="text-red-500 text-sm mt-1">{errors.bath_id}</p>}
            </div>

            {/* Гости */}
            <div>
              <label htmlFor="guests" className="block text-sm font-medium text-gray-700 mb-2">
                Количество гостей
              </label>
              <input
                type="number"
                id="guests"
                name="guests"
                min="1"
                max="12"
                value={formData.guests}
                onChange={handleChange}
                className={`w-full px-4 py-3 border rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent ${
                  errors.guests ? 'border-red-500' : 'border-gray-300'
                }`}
              />
              {errors.guests && <p className="text-red-500 text-sm mt-1">{errors.guests}</p>}
            </div>

            {/* Имя и телефон */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
                  Ваше имя
                </label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="Иван"
                  className={`w-full px-4 py-3 border rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent ${
                    errors.name ? 'border-red-500' : 'border-gray-300'
                  }`}
                />
                {errors.name && <p className="text-red-500 text-sm mt-1">{errors.name}</p>}
              </div>
              <div>
                <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-2">
                  Телефон
                </label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  placeholder="+7 (999) 123-45-67"
                  className={`w-full px-4 py-3 border rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent ${
                    errors.phone ? 'border-red-500' : 'border-gray-300'
                  }`}
                />
                {errors.phone && <p className="text-red-500 text-sm mt-1">{errors.phone}</p>}
              </div>
            </div>

            {/* Email */}
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">
                Email (опционально)
              </label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="ivan@example.com"
                className={`w-full px-4 py-3 border rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent ${
                  errors.email ? 'border-red-500' : 'border-gray-300'
                }`}
              />
              {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
            </div>

            {/* Дополнительно */}
            <div>
              <label htmlFor="notes" className="block text-sm font-medium text-gray-700 mb-2">
                Дополнительно
              </label>
              <textarea
                id="notes"
                name="notes"
                rows="3"
                value={formData.notes}
                onChange={handleChange}
                placeholder="Веники, шашлык, аренда посуды..."
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent resize-none"
              />
            </div>

            {/* Кнопка */}
            <div className="pt-4 text-center">
              <button
                type="submit"
                disabled={loading}
                className={`px-10 py-3 rounded-xl text-lg font-medium transition-all duration-300 ${
                  loading
                    ? 'bg-gray-500 cursor-not-allowed'
                    : 'bg-green-600 hover:bg-green-700 text-white hover:shadow-xl'
                }`}
              >
                {loading ? 'Отправка...' : 'Забронировать'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Booking;
