import { createPortal } from 'react-dom';
import { useState, useEffect } from 'react';
import {
  useGetBathsQuery,
  useGetBroomsQuery,
  useGetMassagesQuery,
  useGetMenuCategoriesQuery,
  useGetAllMenuItemsQuery,
} from '../../../redux/slices/apiSlice';

function BookingDetailsModal({ booking, onClose, onEdit, onDelete }) {
  const [isEditing, setIsEditing] = useState(false);

  // Хуки для редактирования — ВСЕГДА вызываются, даже если не в режиме редактирования
  const { data: baths = [], isLoading: isLoadingBaths } = useGetBathsQuery();
  const { data: brooms = [], isLoading: isLoadingBrooms } = useGetBroomsQuery();
  const { data: massages = [], isLoading: isLoadingMassages } = useGetMassagesQuery();
  const { data: categories = [], isLoading: isLoadingCategories } = useGetMenuCategoriesQuery();
  const { data: allMenuItems = [], isLoading: isLoadingMenuItems } = useGetAllMenuItemsQuery();

  // Состояния формы — тоже всегда существуют
  const [formData, setFormData] = useState({
    bath_id: '',
    start_datetime: '',
    end_datetime: '',
    client_name: '',
    client_phone: '',
    client_email: '',
    notes: '',
    brooms: [],
    menu_items: [],
    massages: [],
  });

  const [selectedCategories, setSelectedCategories] = useState([]);
  const [kitchenItems, setKitchenItems] = useState([]);
  const [showBrooms, setShowBrooms] = useState(false);
  const [showMassages, setShowMassages] = useState(false);
  const [showKitchen, setShowKitchen] = useState(false);

  if (!booking) return null;

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // 🧩 Объединяем все услуги в один массив для удобного вывода
  const allServices = [
    ...(booking.brooms || []).map(item => ({
      type: 'Веник',
      name: item.name,
      price: item.price,
      quantity: item.quantity,
      total: item.price * item.quantity,
    })),
    ...(booking.menu_items || []).map(item => ({
      type: 'Блюдо',
      name: item.name,
      price: item.price,
      quantity: item.quantity,
      total: item.price * item.quantity,
    })),
    ...(booking.massages || []).map(item => ({
      type: 'Массаж',
      name: item.name,
      price: item.cost,
      quantity: item.quantity,
      total: item.cost * item.quantity,
    })),
  ];

  // 💰 Сумма всех услуг
  const servicesTotal = allServices.reduce((sum, item) => sum + item.total, 0);

  // 🏖️ Стоимость только бани = общая стоимость - услуги
  const bathOnlyCost = booking.total_cost - servicesTotal;

  // ============ РЕЖИМ РЕДАКТИРОВАНИЯ ============

  if (isEditing) {
    



    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    };

    const handleSubmit = async (e) => {
      e.preventDefault();

      if (!formData.bath_id || !formData.start_datetime || !formData.end_datetime || !formData.client_name) {
        alert('Заполните обязательные поля: баня, время, имя клиента');
        return;
      }

      if (new Date(formData.start_datetime) >= new Date(formData.end_datetime)) {
        alert('Время окончания должно быть позже начала');
        return;
      }

      try {
        // 🚧 ЗДЕСЬ ДОЛЖЕН БЫТЬ ВЫЗОВ UPDATE, НО ПОКА ИСПОЛЬЗУЕМ onEdit
        // Пример: await updateReservation({ id: booking.reservation_id, ...formData }).unwrap();
        if (onEdit) {
          await onEdit({
            ...formData,
            reservation_id: booking.reservation_id,
          });
        }

        alert('✅ Бронь успешно обновлена!');
        setIsEditing(false); // Возвращаемся в режим просмотра
      } catch (error) {
        console.error('Ошибка обновления брони:', error);
        let message = 'Не удалось обновить бронь';
        if (error.data?.detail) {
          message = error.data.detail;
        }
        alert('❌ ' + message);
      }
    };

    // Управление вениками
    const handleBroomChange = (broomId, quantity) => {
      setFormData(prev => {
        const existing = prev.brooms.find(b => b.broom_id === broomId);
        let updated = [...prev.brooms];

        if (quantity <= 0) {
          updated = updated.filter(b => b.broom_id !== broomId);
        } else {
          if (existing) {
            updated = updated.map(b =>
              b.broom_id === broomId ? { ...b, quantity } : b
            );
          } else {
            updated.push({ broom_id: broomId, quantity });
          }
        }

        return { ...prev, brooms: updated };
      });
    };

    // Управление массажами
    const handleMassageChange = (massageId, quantity) => {
      setFormData(prev => {
        const currentList = prev.massages || [];
        const existing = currentList.find(m => m.massage_id === massageId);
        let updated = [...currentList];

        if (quantity <= 0) {
          updated = updated.filter(m => m.massage_id !== massageId);
        } else {
          if (existing) {
            updated = updated.map(m =>
              m.massage_id === massageId ? { ...m, quantity } : m
            );
          } else {
            updated.push({ massage_id: massageId, quantity });
          }
        }

        return { ...prev, massages: updated };
      });
    };

    // Управление блюдами
    const handleMenuItemChange = (itemId, quantity) => {
      setFormData(prev => {
        const existing = prev.menu_items.find(m => m.menu_item_id === itemId);
        let updated = [...prev.menu_items];

        if (quantity <= 0) {
          updated = updated.filter(m => m.menu_item_id !== itemId);
        } else {
          if (existing) {
            updated = updated.map(m =>
              m.menu_item_id === itemId ? { ...m, quantity } : m
            );
          } else {
            updated.push({ menu_item_id: itemId, quantity });
          }
        }

        return { ...prev, menu_items: updated };
      });
    };

    // При выборе категории — фильтруем товары
    useEffect(() => {
      if (selectedCategories.length > 0 && allMenuItems.length > 0) {
        const filtered = allMenuItems.filter(item =>
          selectedCategories.includes(item.category)
        );
        setKitchenItems(filtered);
      } else {
        setKitchenItems([]);
      }
    }, [selectedCategories, allMenuItems]);

    // Устанавливаем выбранные категории при монтировании, если есть menu_items
    useEffect(() => {
      if (formData.menu_items.length > 0 && allMenuItems.length > 0) {
        const itemCategories = formData.menu_items
          .map(item => {
            const menuItem = allMenuItems.find(m => m.id === item.menu_item_id);
            return menuItem ? menuItem.category : null;
          })
          .filter(Boolean);

        const uniqueCategories = [...new Set(itemCategories)];
        setSelectedCategories(uniqueCategories);
      }
    }, [formData.menu_items, allMenuItems]);

    return createPortal(
      <div
        className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50"
        onClick={handleOverlayClick}
      >
        <div className="bg-white rounded-2xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
          <div className="p-6 border-b border-gray-200 bg-gradient-to-r from-gray-50 to-gray-100">
            <h2 className="text-xl font-semibold text-gray-800">Редактировать бронь</h2>
            <button
              onClick={() => setIsEditing(false)}
              className="absolute top-6 right-6 text-gray-500 hover:text-gray-700 text-2xl"
            >
              ×
            </button>
          </div>

          <form onSubmit={handleSubmit} className="p-6 space-y-6">
            {/* Баня */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Баня *
              </label>
              {isLoadingBaths ? (
                <div className="w-full px-4 py-3 border border-gray-300 rounded-xl bg-gray-100">Загрузка...</div>
              ) : (
                <select
                  name="bath_id"
                  value={formData.bath_id}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
                  required
                >
                  <option value="">Выберите баню</option>
                  {baths.map(bath => (
                    <option key={bath.bath_id} value={bath.bath_id}>
                      {bath.name}
                    </option>
                  ))}
                </select>
              )}
            </div>

            {/* Время */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Начало *
                </label>
                <input
                  type="datetime-local"
                  name="start_datetime"
                  value={formData.start_datetime}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Окончание *
                </label>
                <input
                  type="datetime-local"
                  name="end_datetime"
                  value={formData.end_datetime}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
            </div>

            {/* Клиент */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Имя клиента *
              </label>
              <input
                type="text"
                name="client_name"
                value={formData.client_name}
                onChange={handleChange}
                placeholder="Иван Иванов"
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Телефон
                </label>
                <input
                  type="tel"
                  name="client_phone"
                  value={formData.client_phone}
                  onChange={handleChange}
                  placeholder="+7 (999) 123-45-67"
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Email
                </label>
                <input
                  type="email"
                  name="client_email"
                  value={formData.client_email}
                  onChange={handleChange}
                  placeholder="ivan@example.com"
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            {/* Комментарий */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Комментарий
              </label>
              <textarea
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                rows="3"
                placeholder="Пожелания клиента..."
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* ВЕНИКИ */}
            <div>
              <div className="flex items-center justify-between mb-3">
                <label className="block text-sm font-medium text-gray-700">Веники</label>
                <button
                  type="button"
                  onClick={() => setShowBrooms(!showBrooms)}
                  className="text-blue-600 hover:text-blue-800 font-medium text-sm"
                >
                  {showBrooms ? 'Скрыть' : 'Показать'}
                </button>
              </div>

              {showBrooms && (
                <>
                  {isLoadingBrooms ? (
                    <div className="text-gray-500 py-2">Загрузка веников...</div>
                  ) : (
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3 p-3 bg-gray-50 rounded-xl">
                      {brooms.map(broom => {
                        const current = formData.brooms.find(b => b.broom_id === broom.id);
                        const quantity = current ? current.quantity : 0;
                        return (
                          <div key={broom.id} className="border border-gray-200 rounded-xl p-3 bg-white">
                            <div className="flex justify-between items-center mb-2">
                              <span className="font-medium">{broom.name}</span>
                              <span className="text-green-600 font-semibold">{broom.price} ₽</span>
                            </div>
                            <div className="flex items-center gap-2">
                              <button
                                type="button"
                                onClick={() => handleBroomChange(broom.id, Math.max(0, quantity - 1))}
                                className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center hover:bg-gray-300"
                              >
                                −
                              </button>
                              <span className="w-8 text-center font-medium">{quantity}</span>
                              <button
                                type="button"
                                onClick={() => handleBroomChange(broom.id, quantity + 1)}
                                className="w-8 h-8 rounded-full bg-blue-500 text-white flex items-center justify-center hover:bg-blue-600"
                              >
                                +
                              </button>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  )}
                </>
              )}
            </div>

            {/* МАССАЖИ */}
            <div>
              <div className="flex items-center justify-between mb-3">
                <label className="block text-sm font-medium text-gray-700">Массажи</label>
                <button
                  type="button"
                  onClick={() => setShowMassages(!showMassages)}
                  className="text-blue-600 hover:text-blue-800 font-medium text-sm"
                >
                  {showMassages ? 'Скрыть' : 'Показать'}
                </button>
              </div>

              {showMassages && (
                <>
                  {isLoadingMassages ? (
                    <div className="text-gray-500 py-2">Загрузка массажей...</div>
                  ) : (
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3 p-3 bg-gray-50 rounded-xl">
                      {massages.map(massage => {
                        const current = formData.massages.find(m => m.massage_id === massage.massage_id);
                        const quantity = current ? current.quantity : 0;
                        return (
                          <div key={massage.massage_id} className="border border-gray-200 rounded-xl p-3 bg-white">
                            <div className="flex justify-between items-center mb-2">
                              <span className="font-medium">{massage.name}</span>
                              <span className="text-green-600 font-semibold">{massage.cost} ₽</span>
                            </div>
                            <div className="flex items-center gap-2">
                              <button
                                type="button"
                                onClick={() => handleMassageChange(massage.massage_id, Math.max(0, quantity - 1))}
                                className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center hover:bg-gray-300"
                              >
                                −
                              </button>
                              <span className="w-8 text-center font-medium">{quantity}</span>
                              <button
                                type="button"
                                onClick={() => handleMassageChange(massage.massage_id, quantity + 1)}
                                className="w-8 h-8 rounded-full bg-blue-500 text-white flex items-center justify-center hover:bg-blue-600"
                              >
                                +
                              </button>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  )}
                </>
              )}
            </div>

            {/* КУХНЯ */}
            <div>
              <div className="flex items-center justify-between mb-3">
                <label className="block text-sm font-medium text-gray-700">Кухня</label>
                <button
                  type="button"
                  onClick={() => setShowKitchen(!showKitchen)}
                  className="text-blue-600 hover:text-blue-800 font-medium text-sm"
                >
                  {showKitchen ? 'Скрыть' : 'Показать'}
                </button>
              </div>

              {showKitchen && (
                <>
                  {/* Выбор категории */}
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">Категории</label>
                    <div className="border border-gray-300 rounded-xl p-3 max-h-48 overflow-y-auto bg-white">
                      {isLoadingCategories ? (
                        <div className="text-gray-500">Загрузка категорий...</div>
                      ) : categories.length > 0 ? (
                        categories.map(cat => {
                          const isSelected = selectedCategories.includes(cat.slug);
                          return (
                            <div key={cat.id} className="flex items-center py-2">
                              <input
                                type="checkbox"
                                id={`cat-${cat.id}`}
                                checked={isSelected}
                                onChange={(e) => {
                                  if (e.target.checked) {
                                    setSelectedCategories(prev => [...prev, cat.slug]);
                                  } else {
                                    setSelectedCategories(prev => prev.filter(slug => slug !== cat.slug));
                                  }
                                }}
                                className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500"
                              />
                              <label
                                htmlFor={`cat-${cat.id}`}
                                className="ml-2 text-sm font-medium text-gray-700 cursor-pointer"
                              >
                                {cat.name}
                              </label>
                            </div>
                          );
                        })
                      ) : (
                        <div className="text-gray-500">Нет категорий</div>
                      )}
                    </div>
                  </div>

                  {/* Товары категории */}
                  {isLoadingMenuItems ? (
                    <div className="text-gray-500 py-2">Загрузка блюд...</div>
                  ) : selectedCategories ? (
                    kitchenItems.length > 0 ? (
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-3 p-3 bg-gray-50 rounded-xl">
                        {kitchenItems.map(item => {
                          const current = formData.menu_items.find(m => m.menu_item_id === item.id);
                          const quantity = current ? current.quantity : 0;
                          return (
                            <div key={item.id} className="border border-gray-200 rounded-xl p-3 bg-white">
                              <div className="flex justify-between items-center mb-2">
                                <span className="font-medium">{item.name}</span>
                                <span className="text-green-600 font-semibold">{item.price} ₽</span>
                              </div>
                              <div className="flex items-center gap-2">
                                <button
                                  type="button"
                                  onClick={() => handleMenuItemChange(item.id, Math.max(0, quantity - 1))}
                                  className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center hover:bg-gray-300"
                                >
                                  −
                                </button>
                                <span className="w-8 text-center font-medium">{quantity}</span>
                                <button
                                  type="button"
                                  onClick={() => handleMenuItemChange(item.id, quantity + 1)}
                                  className="w-8 h-8 rounded-full bg-blue-500 text-white flex items-center justify-center hover:bg-blue-600"
                                >
                                  +
                                </button>
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    ) : (
                      <div className="text-gray-500 text-sm py-2">В этой категории пока нет блюд</div>
                    )
                  ) : (
                    <div className="text-gray-500 text-sm py-2">Сначала выберите категорию</div>
                  )}
                </>
              )}
            </div>

            {/* Кнопки */}
            <div className="pt-4 border-t border-gray-200 flex gap-3">
              <button
                type="submit"
                className="flex-1 bg-green-600 text-white py-3 px-6 rounded-xl font-medium hover:bg-green-700 transition"
              >
                Сохранить изменения
              </button>
              <button
                type="button"
                onClick={() => setIsEditing(false)}
                className="flex-1 bg-gray-200 text-gray-800 py-3 px-6 rounded-xl font-medium hover:bg-gray-300 transition"
              >
                Отмена
              </button>
            </div>
          </form>
        </div>
      </div>,
      document.body
    );
  }

  // ============ РЕЖИМ ПРОСМОТРА ============

  return createPortal(
    <div
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50"
      onClick={handleOverlayClick}
    >
      <div className="bg-white rounded-2xl shadow-2xl max-w-lg w-full max-h-[90vh] overflow-y-auto">
        {/* Заголовок */}
        <div className="p-6 border-b border-gray-200 bg-gradient-to-r from-gray-50 to-gray-100">
          <h2 className="text-xl font-semibold text-gray-800">Детали брони</h2>
          <button
            onClick={onClose}
            className="absolute top-6 right-6 text-gray-500 hover:text-gray-700 text-2xl"
          >
            ×
          </button>
        </div>

        <div className="p-6 space-y-6">
          {/* Информация о клиенте */}
          <div>
            <h3 className="text-lg font-medium text-gray-800 mb-3">Клиент</h3>
            <div className="space-y-2 text-sm text-gray-700">
              <p><strong>Имя:</strong> {booking.client_name}</p>
              <p><strong>Телефон:</strong> {booking.client_phone}</p>
              {booking.client_email && (
                <p><strong>Email:</strong> {booking.client_email}</p>
              )}
            </div>
          </div>

          {/* Баня и время */}
          <div>
            <h3 className="text-lg font-medium text-gray-800 mb-3">Баня и время</h3>
            <div className="space-y-2 text-sm text-gray-700">
              <p><strong>Баня:</strong> {booking.bath?.name || 'Не указана'}</p>
              <p>
                <strong>Время:</strong>{' '}
                {new Date(booking.start_datetime).toLocaleString('ru-RU', {
                  day: 'numeric',
                  month: 'long',
                  hour: '2-digit',
                  minute: '2-digit',
                })}
                {' – '}
                {new Date(booking.end_datetime).toLocaleString('ru-RU', {
                  day: 'numeric',
                  month: 'long',
                  hour: '2-digit',
                  minute: '2-digit',
                })}
              </p>
            </div>
          </div>

          {/* Услуги */}
          {allServices.length > 0 && (
            <div>
              <h3 className="text-lg font-medium text-gray-800 mb-3">Дополнительные услуги</h3>
              <ul className="space-y-3">
                {allServices.map((service, idx) => (
                  <li
                    key={idx}
                    className="flex justify-between items-center p-3 bg-gray-50 rounded-lg border border-gray-200"
                  >
                    <div>
                      <span className="font-medium text-gray-800">{service.name}</span>
                      <span className="text-xs text-gray-500 ml-2">({service.type})</span>
                      <div className="text-sm text-gray-600">
                        {service.price} ₽ × {service.quantity} шт
                      </div>
                    </div>
                    <span className="font-bold text-green-600">{service.total} ₽</span>
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Комментарий */}
          {booking.notes && (
            <div>
              <h3 className="text-lg font-medium text-gray-800 mb-3">Комментарий</h3>
              <p className="text-sm text-gray-700 italic p-3 bg-gray-50 rounded-lg border border-gray-200">
                {booking.notes}
              </p>
            </div>
          )}

          {/* Чек */}
          <div className="bg-gray-50 p-4 rounded-xl border border-gray-200">
            <h3 className="text-lg font-medium text-gray-800 mb-3">Чек</h3>
            <div className="space-y-2 text-sm text-gray-700">
              <div className="flex justify-between">
                <span>Стоимость бани:</span>
                <strong>{bathOnlyCost} ₽</strong>
              </div>
              <div className="flex justify-between">
                <span>Дополнительные услуги:</span>
                <strong>{servicesTotal} ₽</strong>
              </div>
              <hr className="my-2 border-gray-300" />
              <div className="flex justify-between font-bold text-lg">
                <span>Итого к оплате:</span>
                <span className="text-green-600">{booking.total_cost} ₽</span>
              </div>
            </div>
          </div>
        </div>

        {/* Кнопки */}
        <div className="p-6 border-t border-gray-200 flex flex-col sm:flex-row gap-3">
          <button
            onClick={() => setIsEditing(true)}
            className="flex-1 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition font-medium"
          >
            Редактировать
          </button>
          <button
            onClick={() => onDelete(booking.reservation_id)}
            className="flex-1 bg-red-100 text-red-700 py-2 px-4 rounded-lg hover:bg-red-200 transition font-medium"
          >
            Удалить
          </button>
          <button
            onClick={onClose}
            className="flex-1 bg-gray-200 text-gray-800 py-2 px-4 rounded-lg hover:bg-gray-300 transition font-medium"
          >
            Закрыть
          </button>
        </div>
      </div>
    </div>,
    document.body
  );
}

export default BookingDetailsModal;
