// redux/slices/apiSlice.js
import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

export const apiSlice = createApi({
  reducerPath: 'api',
  baseQuery: fetchBaseQuery({ baseUrl: 'http://127.0.0.1:8000/api' }),
  tagTypes: ['Baths', 'Bookings'],
  endpoints: (builder) => ({
    // --- Получение всех бань ---
    // apiSlice.js
    getBaths: builder.query({
      query: () => '/baths/',
      transformResponse: (response) => {
  console.log('Raw response from /baths:', response);

  const transformed = response.map(bath => ({
    ...bath,
    image: bath.photos?.[0]?.image_url || '/img/placeholder.jpg',
    path: `/baths/${bath.bath_id}`,
    subtitle: bath.title,
  }));

  console.log('Transformed baths:', transformed);
  return transformed;
},
      providesTags: ['Baths'],
    }),

    // --- Получение одной бани ---
    getBathById: builder.query({
      query: (id) => `/baths/${id}`,
      transformResponse: (response) => {
        return {
          ...response,
          image: response.photos?.[0]?.image_url || '/img/placeholder.jpg',
          images: response.photos?.map(p => p.image_url) || [],
          path: `/baths/${response.bath_id}`,
          subtitle: response.title,
        };
      },
      providesTags: ['Bath'],
    }),

    // --- Создание брони ---
    createBooking: builder.mutation({
      query: (bookingData) => ({
        url: '/bookings/',
        method: 'POST',
        body: bookingData,
      }),
      invalidatesTags: ['Bookings'],
    }),
  }),
});

export const {
  useGetBathsQuery,
  useGetBathByIdQuery,
  useCreateBookingMutation
} = apiSlice;
