import { useState } from 'react';

function ReservationsFilters({ onApply }) {
  const [filters, setFilters] = useState({
    bathId: '',
    date: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleApply = () => {
    if (!filters.bathId || !filters.date) {
      alert('Выберите баню и дату');
      return;
    }
    onApply(filters);
  };

  return (
    <div className="bg-white p-6 rounded-2xl shadow-md border border-gray-200 mb-8">
      <h2 className="text-xl font-semibold text-gray-800 mb-4">Фильтры</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 items-end">
        <div>
          <label htmlFor="bathId" className="block text-sm font-medium text-gray-700 mb-2">
            Выберите баню
          </label>
          <select
            id="bathId"
            name="bathId"
            value={filters.bathId}
            onChange={handleChange}
            className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
          >
            <option value="">Все бани</option>
            <option value="1">Кедровая баня</option>
            <option value="2">Нефритовая баня</option>
            <option value="3">Липовая баня</option>
          </select>
        </div>

        <div>
          <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-2">
            Выберите дату
          </label>
          <input
            type="date"
            id="date"
            name="date"
            value={filters.date}
            onChange={handleChange}
            className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
          />
        </div>

        <div>
          <button
            type="button"
            onClick={handleApply}
            className="w-full bg-green-600 text-white py-3 rounded-xl font-medium hover:bg-green-700 transition"
          >
            Показать расписание
          </button>
        </div>
      </div>
    </div>
  );
}

export default ReservationsFilters;
