import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import { NavLink } from 'react-router-dom';

import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

const bathsList = [
  [1, 'Кедровая баня', 'Русская кедровая баня на берёзовых дровах', '/img/Kedrovaya-1.png', '/kedrovaya'],
  [2, 'Нефритовая баня', 'Русская нефритовая баня на берёзовых дровах', '/img/bg-home.png', '/nefritovaya']
];

function SliderBaths() {
  return (
    <div className="relative">
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={0}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        autoplay={{
          delay: 4000,
          disableOnInteraction: false,
        }}
        loop
        speed={800}
        className="my-swiper"
      >
        {bathsList.map(([id, name, title, img, path]) => (
          <SwiperSlide key={id}>
            <div className="relative flex items-center h-[600px] bg-gray-100">
              {/* Картинка */}
              <img
                src={img}
                alt={name}
                className="absolute inset-0 w-full h-full object-cover"
              />

              {/* Полупрозрачный слой поверх картинки */}
              <div className="absolute inset-0 bg-black bg-opacity-40"></div>

              {/* Текст и кнопки — поверх картинки */}
              <div className="relative z-10 flex w-full px-20">
                {/* Левая часть — текст */}
                <div className="w-1/2 text-white">
                  <h1 className="text-4xl font-bold mb-2 transform -translate-y-1/2">
                    {name}
                  </h1>
                  <p className="text-lg mb-6">{title}</p>

                  {/* Кнопки — прижаты к нижнему краю */}
                  <div className="absolute bottom-10 flex justify-between w-1/2 px-20">
                    <NavLink
                      to="/baths"
                      className="bg-transparent border border-white text-white px-6 py-2 rounded hover:bg-white hover:text-gray-800 transition"
                    >
                      Все бани
                    </NavLink>
                    <NavLink
                      to={path}
                      className="bg-amber-600 text-white px-6 py-2 rounded hover:bg-amber-700 transition"
                    >
                      Подробнее
                    </NavLink>
                  </div>
                </div>

                {/* Правая часть — можно оставить пустой или добавить что-то */}
                <div className="w-1/2"></div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
}

export default SliderBaths;
