import { useState, useMemo } from 'react';
import ReservationsFilters from '../Reservations/ReservationsFilters';
import BookingDetailsModal from '../Reservations/BookingDetailsModal';
import AddBookingModal from '../Reservations/AddBookingModal';
import { useGetBathsQuery, useGetReservationsByDateQuery } from '../../../redux/slices/apiSlice';

function AdminReservations() {
  const today = new Date().toISOString().split('T')[0];
  const [filters, setFilters] = useState({ date: today });
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);

  // Получаем ВСЕ бани
  const { data: baths = [], isLoading: isLoadingBaths } = useGetBathsQuery();
  // Получаем брони на выбранную дату
  const { data: reservations = [], isLoading: isLoadingReservations } = useGetReservationsByDateQuery({
    date: filters.date || today,
  });

  // Генерация слотов 9:00 - 23:30 в UTC
  const timeSlots = useMemo(() => {
    const slots = [];
    for (let hour = 9; hour < 24; hour++) {
      for (let minute of [0, 30]) {
        const timeStr = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
        // Генерируем слоты в UTC
        const dateTime = new Date(`${filters.date}T${timeStr}:00Z`);
        slots.push({
          time: timeStr,
          start: dateTime,
          end: new Date(dateTime.getTime() + 30 * 60000),
        });
      }
    }
    return slots;
  }, [filters.date]);

  // Формируем список бань с бронями
  const bathsToDisplay = useMemo(() => {
    if (!Array.isArray(baths) || baths.length === 0) {
      return [];
    }

    return baths.map(bath => {
      const bathId = bath.bath_id !== undefined ? bath.bath_id : bath.id;
      if (bathId === undefined) {
        console.warn('⚠️ У бани отсутствует и bath_id, и id:', bath);
        return null;
      }

      const bookings = (Array.isArray(reservations) ? reservations : []).filter(res => {
        if (!res || !res.bath_id || !res.start_datetime) return false;
        const bookingDate = res.start_datetime.split('T')[0];
        return res.bath_id === bathId && bookingDate === filters.date;
      });

      return {
        bath_id: bathId,
        bathName: bath.name || 'Без названия',
        bookings: bookings,
        hasBookings: bookings.length > 0,
      };
    }).filter(Boolean);
  }, [baths, reservations, filters.date]);

  // Формат времени с учетом +05:00
  const formatTime = (isoString) => {
    const date = new Date(isoString);
    // Конвертируем в +05:00
    const offset = 5 * 60; // +05:00 в минутах
    const localTime = new Date(date.getTime() + (offset - date.getTimezoneOffset()) * 60000);
    return localTime.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });
  };

  // Обработчики
  const handleApply = (newFilters) => {
    setFilters(newFilters);
  };

  const handleViewBooking = (booking) => {
    setSelectedBooking(booking);
  };

  const handleCloseModal = () => {
    setSelectedBooking(null);
  };

  const handleAddBooking = () => {
    setIsAddModalOpen(true);
  };

  // Загрузка
  if (isLoadingBaths || isLoadingReservations) {
    return (
      <div className="flex items-center justify-center py-16">
        <div className="text-gray-500">Загрузка...</div>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="text-2xl font-semibold text-gray-800 mb-6">
        Записи на {new Date(filters.date).toLocaleDateString('ru-RU', {
          day: 'numeric',
          month: 'long',
          year: 'numeric',
        })}
      </h2>

      <ReservationsFilters
        onApply={handleApply}
        onAddBooking={handleAddBooking}
      />

      <div className="overflow-x-auto pb-4 -mx-4 px-4">
        <div className="inline-flex space-x-6 min-h-[600px]">
          {/* Колонка времени */}
          <div className="flex-shrink-0 w-32 bg-gray-50 p-4 border-r border-gray-200 sticky top-0">
            <div className="text-sm font-medium text-gray-700 mb-2">ВРЕМЯ</div>
            {/* Пустая строка для выравнивания с таблицами */}
            <div className="h-14"></div>
            {timeSlots.map((slot, idx) => (
              <div key={idx} className="text-sm text-gray-600 py-2 h-14 flex items-center">
                {slot.time} {/* Отображаем просто строку времени, без локализации */}
              </div>
            ))}
          </div>

          {/* Колонки с банями */}
          {bathsToDisplay.length === 0 ? (
            <div className="text-gray-500 p-8">Нет доступных бань</div>
          ) : (
            bathsToDisplay.map((bath) => (
              <div key={bath.bath_id} className="flex-shrink-0 w-96">
                <div className="bg-white rounded-xl shadow-md border border-gray-200 overflow-hidden">
                  <div className="p-4 border-b border-gray-200 bg-gray-50">
                    <h3 className="text-lg font-semibold text-gray-800">{bath.bathName}</h3>
                  </div>

                  {/* Таблица временных слотов */}
                  <div className="overflow-x-auto">
                    <table className="w-full table-fixed border-separate border-spacing-0">
                      <tbody>
                        {/* Пустая строка для выравнивания с колонкой времени */}
                        <tr>
                          <td className="px-6 py-0 h-14"></td>
                        </tr>
                        {timeSlots.map((slot, idx) => {
                          // Конвертируем слот в +05:00 для сравнения с бронями
                          const offsetMs = 5 * 60 * 60 * 1000; // +05:00
                          const slotStartPlus5 = new Date(slot.start.getTime() + offsetMs);
                          const slotEndPlus5 = new Date(slot.end.getTime() + offsetMs);

                          // Находим бронь, которая начинается в этом слоте (в +05:00)
                          const bookingStartsHere = bath.bookings.find(booking => {
                            const start = new Date(booking.start_datetime);
                            return start >= slotStartPlus5 && start < slotEndPlus5;
                          });

                          // Проверяем пересечение
                          const anyBookingOverlaps = bath.bookings.some(booking => {
                            const start = new Date(booking.start_datetime);
                            const end = new Date(booking.end_datetime);
                            return start < slotEndPlus5 && end > slotStartPlus5;
                          });

                          return (
                            <tr key={idx} className="hover:bg-gray-25 transition-colors">
                              <td className="px-6 py-0 relative bg-gray-50" style={{ height: '56px', overflow: 'visible' }}>
                                {bookingStartsHere ? (
                                  <div
                                    className="absolute inset-x-0 bg-red-50 border-4 border-red-600 text-red-800 text-sm font-medium rounded-lg px-3 flex items-center justify-center cursor-pointer hover:shadow transition-all duration-200 group"
                                    style={{
                                      top: 0,
                                      height: `${((new Date(bookingStartsHere.end_datetime) - new Date(bookingStartsHere.start_datetime)) / (30 * 60000)) * 100}%`,
                                      zIndex: 10,
                                      overflow: 'visible',
                                    }}
                                    title={`${bookingStartsHere.client_name} • ${formatTime(bookingStartsHere.start_datetime)} – ${formatTime(bookingStartsHere.end_datetime)}`}
                                    onClick={() => handleViewBooking(bookingStartsHere)}
                                  >
                                    <div className="w-1 bg-red-400 rounded-full absolute left-0 top-1 bottom-1 group-hover:w-2 transition-all duration-200"></div>
                                    <span className="truncate max-w-full text-center leading-tight font-medium">
                                      {bookingStartsHere.client_name}
                                    </span>
                                  </div>
                                ) : !anyBookingOverlaps ? (
                                  <div className="absolute inset-0 flex items-center justify-center text-green-600 text-sm font-medium bg-green-50 rounded-lg border border-green-200">
                                    Свободно
                                  </div>
                                ) : null}
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      <BookingDetailsModal
        booking={selectedBooking}
        onClose={handleCloseModal}
        onEdit={() => { }}
        onDelete={() => { }}
      />

      <AddBookingModal
        isOpen={isAddModalOpen}
        onClose={() => setIsAddModalOpen(false)}
        onAdd={() => { }}
      />
    </div>
  );
}

export default AdminReservations;
