// src/pages/Admin/Reservations/ReservationsTable.jsx

import { useEffect, useState } from 'react';

function ReservationsTable({ reservations = [], date, bathId, onViewBooking }) {
  const [timeSlots, setTimeSlots] = useState([]);
  const slotDurationMinutes = 30; // Шаг: 30 минут

  // Генерация временных слотов: 9:00 → 24:00
  useEffect(() => {
    if (!date) return;

    const slots = [];
    const startHour = 9;
    const endHour = 24;

    for (let hour = startHour; hour < endHour; hour++) {
      for (let minute of [0, 30]) {
        const timeStr = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
        const dateTime = new Date(`${date}T${timeStr}:00`);
        slots.push({
          time: timeStr,
          start: dateTime,
          end: new Date(dateTime.getTime() + slotDurationMinutes * 60000),
        });
      }
    }

    setTimeSlots(slots);
  }, [date]);

  // Форматируем время (чч:мм)
  const formatTime = (date) => {
    return date.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });
  };

  // Получаем все брони на выбранную баню и дату
  const filteredReservations = reservations.filter((res) => {
    const resDate = new Date(res.start_datetime).toDateString();
    return res.bath_id === Number(bathId) && new Date(date).toDateString() === resDate;
  });

  // Приводим брони к формату с объектами времени
  const normalizedReservations = filteredReservations.map((res) => ({
    ...res,
    start: new Date(res.start_datetime),
    end: new Date(res.end_datetime),
  }));

  if (!date || !bathId) {
    return (
      <div className="text-center py-16 text-gray-500 bg-gray-50 rounded-2xl">
        <p>Выберите баню и дату, чтобы посмотреть расписание</p>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-2xl shadow-md border border-gray-200 overflow-hidden">
      <div className="p-6 border-b border-gray-200 bg-gradient-to-r from-green-50 to-emerald-50">
        <h2 className="text-xl font-semibold text-gray-800">
          Расписание на {new Date(date).toLocaleDateString('ru-RU', {
            day: 'numeric',
            month: 'long',
            year: 'numeric'
          })}
        </h2>
      </div>

      <div className="overflow-x-auto">
        <table className="w-full table-fixed border-separate border-spacing-0">
          <thead className="bg-gray-50">
            <tr>
              <th className="w-32 px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider sticky left-0 bg-gray-50 z-10">
                Время
              </th>
              <th className="min-w-[400px] px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Статус
              </th>
            </tr>
          </thead>
          <tbody>
            {timeSlots.map((slot, idx) => {
              // Ищем пересекающиеся брони
              const overlapping = normalizedReservations.find((res) =>
                res.start < slot.end && res.end > slot.start
              );

              return (
                <tr key={idx} className="border-b border-gray-100 hover:bg-gray-25 transition-colors">
                  {/* Время */}
                  <td className="px-6 py-4 text-sm font-medium text-gray-800 bg-gray-50 border-r border-gray-200 sticky left-0 z-0">
                    {formatTime(slot.start)}
                  </td>

                  {/* Ячейка бронирования */}
                  <td className="px-6 py-0 h-14 relative bg-gray-50">
                    {overlapping ? (
                      <div
                        className="absolute inset-y-0 left-0 right-0 bg-red-50 border border-red-200 text-red-800 text-sm font-medium rounded-lg px-3 flex items-center justify-center cursor-pointer hover:shadow transition-all duration-200 group"
                        style={{
                          top: `${((overlapping.start - slot.start) / (1000 * 60)) / slotDurationMinutes * 100}%`,
                          height: `${((overlapping.end - overlapping.start) / (1000 * 60)) / slotDurationMinutes * 100}%`,
                        }}
                        title={`${overlapping.client_name} • ${formatTime(overlapping.start)} – ${formatTime(overlapping.end)}`}
                        onClick={() => onViewBooking(overlapping)}
                      >
                        {/* Индикатор слева */}
                        <div className="w-1 bg-red-400 rounded-full absolute left-0 top-1 bottom-1 group-hover:w-2 transition-all duration-200"></div>
                        
                        <span className="truncate max-w-full text-center leading-tight font-medium">
                          {overlapping.client_name}
                        </span>
                      </div>
                    ) : (
                      <div className="absolute inset-0 flex items-center justify-center text-green-600 text-sm font-medium bg-green-50 rounded-lg border border-green-200">
                        Свободно
                      </div>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* Легенда */}
      <div className="p-4 bg-gray-50 border-t border-gray-200 flex flex-wrap gap-4 text-sm">
        <div className="flex items-center space-x-2">
          <div className="w-4 h-4 bg-green-50 border border-green-200 rounded"></div>
          <span>Свободно</span>
        </div>
        <div className="flex items-center space-x-2">
          <div className="w-4 h-4 bg-red-50 border border-red-200 rounded flex items-center justify-center">
            <div className="w-1 h-6 bg-red-400 rounded"></div>
          </div>
          <span>Занято</span>
        </div>
      </div>
    </div>
  );
}

export default ReservationsTable;
