import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import CustomButton from '../../../../components/UI/CustomButton/CustomButton';

import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

const bathsList = [
  [1, 'Кедровая баня', 'Русская кедровая баня на берёзовых дровах', '/img/Kedrovaya-1.png', '/kedrovaya'],
  [2, 'Нефритовая баня', 'Русская нефритовая баня на берёзовых дровах', '/img/bg-home.png', '/nefritovaya']
];

function SliderBaths() {
  return (
    <div className="relative h-[640px] overflow-hidden">
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={0}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        autoplay={{
          delay: 4000,
          disableOnInteraction: false,
        }}
        loop
        speed={800}
        className="h-full rounded-2xl shadow-2xl"
      >
        {bathsList.map(([id, name, title, img, path]) => (
          <SwiperSlide key={id}>
            <div className="flex items-center h-full bg-gradient-to-r from-amber-50 to-orange-50">
              {/* Картинка слева */}
              <div className="flex-shrink-0 ml-16">
                <img
                  src={img}
                  alt={name}
                  className="w-96 h-80 object-cover rounded-2xl shadow-xl transform transition-transform hover:scale-105"
                />
              </div>

              {/* Текст и кнопки справа */}
              <div className="flex flex-col h-full justify-center pl-16 pr-20 w-full max-w-2xl">
                <div className="space-y-6 text-gray-800">
                  <h1 className="text-6xl font-light leading-tight transform -translate-y-2">
                    {name}
                  </h1>
                  <p className="text-xl font-extralight text-gray-600 leading-relaxed max-w-lg">
                    {title}
                  </p>
                </div>

                {/* Кнопки — прижаты к низу */}
                <div className="flex gap-8 mt-auto mb-8">
                  <CustomButton to={path} variant="brown" text="Подробнее" />
                  <CustomButton to="/baths" variant="green" text="Все бани" />
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>

      {/* Лёгкая тень внизу (для глубины) */}
      <div className="absolute bottom-0 left-0 w-full h-16 bg-gradient-to-t from-black/5 to-transparent pointer-events-none"></div>
    </div>
  );
}

export default SliderBaths;
