import SliderItem from "./SliderItem/SliderItem";
const images = [
    '/img/Logo.png',
    '/img/bg-home.png'
];

const bathsList = [
    ['Кедровая баня', 'Русская кедровая баня на берёзовых дровах', '/img/Kedrovaya-1.png'],
    ['Нефритовая баня', 'Русская нефритовая баня на берёзовых дровах', '/img/bg-home.png']

]

function Slider() {
    return (
        <div>
            <Swiper
                modules={[Navigation, Pagination, Autoplay]}
                spaceBetween={0}
                slidesPerView={1}
                navigation
                pagination={{ clickable: true }}
                autoplay={{
                    delay: 4000,
                    disableOnInteraction: false,
                }}
                loop
                speed={800}
                className="my-swiper"
            >   
                <div>

                {images.map((src, index) => (
                    <SwiperSlide key={index}>
                        <div
                            style={{
                                height: '500px',
                                backgroundImage: `url(${src})`,
                                backgroundSize: 'cover',
                                backgroundPosition: 'center',
                                borderRadius: '16px',
                                boxShadow: '0 10px 30px rgba(0,0,0,0.2)',
                            }}
                        />
                    </SwiperSlide>
                ))}
                </div>
            </Swiper>
        </div>
    );
}

export default Slider;
