// src/pages/Admin/Reservations/ReservationsFilters.jsx

import { useState, useEffect } from 'react';

function ReservationsFilters({ onApply }) {
  const today = new Date().toISOString().split('T')[0]; // "YYYY-MM-DD"

  const [date, setDate] = useState(today);

  // При монтировании — сразу применяем сегодняшнюю дату
  useEffect(() => {
    onApply({ date });
  }, [onApply]);

  const handleApply = () => {
    onApply({ date });
  };

  return (
    <div className="bg-white p-6 rounded-2xl shadow-md border border-gray-200 mb-8">
      <h2 className="text-xl font-semibold text-gray-800 mb-4">Фильтры</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 items-end">
        <div>
          <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-2">
            Выберите дату
          </label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
          />
        </div>

        <div>
          <button
            type="button"
            onClick={handleApply}
            className="w-full bg-green-600 text-white py-3 rounded-xl font-medium hover:bg-green-700 transition"
          >
            Показать расписание
          </button>
        </div>
      </div>
    </div>
  );
}

export default ReservationsFilters;
