// src/store/store.js
import { configureStore } from '@reduxjs/toolkit';
import { apiSlice } from './slices/apiSlice'; // üëà –û—Å—Ç–∞–≤–ª—è–µ–º –∫–∞–∫ –µ—Å—Ç—å (–¥–ª—è –¥—Ä—É–≥–∏—Ö —ç–Ω–¥–ø–æ–∏–Ω—Ç–æ–≤)
import bookingReducer from './slices/bookingSlice';
import { massagesApiSlice } from './slices/massagesApiSlice'; // üëà –ù–æ–≤—ã–π RTK Query slice –¥–ª—è –º–∞—Å—Å–∞–∂–µ–π

export const store = configureStore({
  reducer: {
    [apiSlice.reducerPath]: apiSlice.reducer, // üëà –°—Ç–∞—Ä—ã–π API-—Å–ª–∞–π—Å (–Ω–µ —Ç—Ä–æ–≥–∞–µ–º!)
    booking: bookingReducer, // üëà –û—Å—Ç–∞—ë—Ç—Å—è –±–µ–∑ –∏–∑–º–µ–Ω–µ–Ω–∏–π
    [massagesApiSlice.reducerPath]: massagesApiSlice.reducer, // üëà –ù–û–í–´–ô —Å–ª–∞–π—Å –¥–ª—è –º–∞—Å—Å–∞–∂–µ–π
  },
  middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware()
      .concat(apiSlice.middleware)         // üëà –°—Ç–∞—Ä—ã–π middleware (–æ—Å—Ç–∞—ë—Ç—Å—è!)
