// src/pages/Roadmap/Roadmap.jsx

import { useState } from 'react';

function Roadmap() {
  const [expanded, setExpanded] = useState({});

  const toggleSection = (id) => {
    setExpanded((prev) => ({ ...prev, [id]: !prev[id] }));
  };

  const roadmapData = [
    {
      id: 1,
      title: 'Авторизация и безопасность',
      status: 'done', // done, in-progress, planned
      color: 'bg-green-500',
      items: [
        'Настроить JWT авторизацию',
        'Создать страницу /admin/login',
        'Защитить маршруты',
        'Хранение токена в localStorage',
      ],
    },
    {
      id: 2,
      title: 'Управление заявками',
      status: 'in-progress',
      color: 'bg-blue-500',
      items: [
        'Просмотр новых и прочитанных заявок',
        'Модальное окно с деталями',
        'Отметка как прочитанное',
      ],
    },
    {
      id: 3,
      title: 'Таблица бронирований',
      status: 'planned',
      color: 'bg-gray-400',
      items: [
        'Фильтры: дата, баня',
        'Таблица с 30-минутными слотами',
        'Цветовое выделение занятых интервалов',
      ],
    },
    {
      id: 4,
      title: 'Ручное бронирование',
      status: 'planned',
      color: 'bg-gray-400',
      items: [
        'Модалка создания брони',
        'Выбор услуг и расчёт стоимости',
        'Редактирование и удаление',
      ],
    },
    {
      id: 5,
      title: 'Будущие улучшения',
      status: 'planned',
      color: 'bg-purple-500',
      items: [
        'Настраиваемый шаг времени (15/30/60 мин)',
        'Экспорт в Excel',
        'Telegram-уведомления',
        'WebSocket для автообновления',
      ],
    },
  ];

  const getStatusText = (status) => {
    return {
      done: 'Готово',
      'in-progress': 'В работе',
      planned: 'Запланировано',
    }[status];
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-10">
          <h1 className="text-4xl font-light text-gray-800 mb-3">Роадмап разработки</h1>
          <p className="text-lg text-gray-600">
            Этапы реализации админ-панели для управления баней
          </p>
        </div>

        <div className="space-y-6">
          {roadmapData.map((phase) => (
            <div
              key={phase.id}
              className="bg-white rounded-2xl shadow-lg border border-gray-200 overflow-hidden"
            >
              {/* Заголовок этапа */}
              <button
                onClick={() => toggleSection(phase.id)}
                className="w-full px-8 py-6 text-left flex items-center justify-between hover:bg-gray-50 transition"
              >
                <div className="flex items-center space-x-4">
                  <span className={`w-4 h-4 rounded-full ${phase.color}`}></span>
                  <h2 className="text-xl font-medium text-gray-800">{phase.title}</h2>
                </div>
                <div className="flex items-center space-x-4">
                  <span
                    className={`text-sm px-3 py-1 rounded-full ${
                      phase.status === 'done'
                        ? 'bg-green-100 text-green-800'
                        : phase.status === 'in-progress'
                        ? 'bg-blue-100 text-blue-800'
                        : 'bg-gray-100 text-gray-800'
                    }`}
                  >
                    {getStatusText(phase.status)}
                  </span>
                  <svg
                    className={`w-5 h-5 text-gray-500 transition-transform ${
                      expanded[phase.id] ? 'rotate-180' : ''
                    }`}
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
                  </svg>
                </div>
              </button>

              {/* Список задач */}
              {expanded[phase.id] && (
                <div className="px-8 pb-6 pt-2">
                  <ul className="space-y-2">
                    {phase.items.map((item, idx) => (
                      <li
                        key={idx}
                        className="flex items-start space-x-3 text-gray-700"
                      >
                        <span className="w-2 h-2 bg-gray-400 rounded-full mt-2 flex-shrink-0"></span>
                        <span className="text-sm">{item}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Легенда */}
        <div className="mt-12 bg-white rounded-xl p-6 shadow-md border border-gray-200">
          <h3 className="text-lg font-medium text-gray-800 mb-3">Статусы</h3>
          <div className="flex flex-wrap gap-6 text-sm text-gray-600">
            <div className="flex items-center space-x-2">
              <span className="w-3 h-3 bg-green-500 rounded-full"></span>
              <span>Готово</span>
            </div>
            <div className="flex items-center space-x-2">
              <span className="w-3 h-3 bg-blue-500 rounded-full"></span>
              <span>В работе</span>
            </div>
            <div className="flex items-center space-x-2">
              <span className="w-3 h-3 bg-gray-400 rounded-full"></span>
              <span>Запланировано</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Roadmap;
