// src/redux/store.js
import { configureStore } from '@reduxjs/toolkit';
import { apiSlice } from './slices/apiSlice'; // üëà –û—Å—Ç–∞–≤–ª—è–µ–º –∫–∞–∫ –µ—Å—Ç—å
import bookingReducer from './slices/bookingSlice'; // üëà –û—Å—Ç–∞–≤–ª—è–µ–º –∫–∞–∫ –µ—Å—Ç—å
import { massagesApiSlice } from './slices/massagesApiSlice'; // üëà –ù–æ–≤—ã–π RTK Query

export const store = configureStore({
  reducer: {
    [apiSlice.reducerPath]: apiSlice.reducer,     
    booking: bookingReducer,                  

  },
  middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware()
      .concat(apiSlice.middleware)
      .concat(massagesApiSlice.middleware), // üëà –û–±—è–∑–∞—Ç–µ–ª—å–Ω–æ –¥–æ–±–∞–≤–ª—è–µ–º!
});
