import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import { NavLink } from 'react-router-dom';

import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

const bathsList = [
  [1, 'Кедровая баня', 'Русская кедровая баня на берёзовых дровах', '/img/Kedrovaya-1.png', '/kedrovaya'],
  [2, 'Нефритовая баня', 'русская нефритовая баня на берёзовых дровах', '/img/bg-home.png', '/nefritovaya']
];

function SliderBaths() {
  return (
    <div className="relative h-[600px] bg-amber-800">
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={0}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        autoplay={{
          delay: 4000,
          disableOnAction: false,
        }}
        loop
        speed={800}
        className="my-swiper"
      >
        {bathsList.map(([id, name, title, img, path]) => (
          <SwiperSlide key={id}>
            <div className="flex items-center h-full">
              {/* Картинка слева*/}
              <div className="w-1/2 flex justify-center items-center">
                <img
                  src={img}
                  alt={name}
                  className="w-96 h-72 object-cover rounded-lg shadow-lg"
                />
              </div>

              {/*Текст и кнопкисправа*/}
              <div className="w-1/2 px-10 py-16 text-white">
                <h1 className="text-4xl font-bold mb-4">{name}</h1>
                <p className="text-lg mb-8 leading-rel">{title}</p>

                <div className="flex gap-4 mt-6">
                  <NavLink
                    to={path}
                    className="bg-amber-700 hover:bg-amber-600 text-white px-6 py-3 rounded-lg transition"
                  >
                    Подробнее
                  </NavLink>
                  <NavLink
                    to="/baths"
                    className="bg-green-600hover:bg-green-500 text-white px-6 py-3rounded-lg transition"
                  >
                    Все бани
                  </NavLink>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
}

export default SliderBaths;
