import CustomButton from "../../components/UI/CustomButton/CustomButton";


const massages = [
  {
    id: 1,
    name: "Классический релакс-массаж",
    description: "Мягкое расслабление мышц после парной. Снимает усталость и напряжение.",
    price: "1500 ₽/час",
  },
  {
    id: 2,
    name: "Медовый массаж",
    description: "Очищает кожу и улучшает кровообращение. Натуральный состав, эффект детоксикации.",
    price: "1800 ₽/час",
  },
  {
    id: 3,
    name: "Антицеллюлитный массаж",
    description: "Активное воздействие на проблемные зоны. Повышает упругость кожи.",
    price: "2000 ₽/час",
  },
  {
    id: 4,
    name: "Спортивный массаж",
    description: "Для восстановления после нагрузок. Ускоряет регенерацию мышц.",
    price: "1900 ₽/час",
  },
  {
    id: 5,
    name: "Баночный массаж",
    description: "Вакуумное воздействие улучшает лимфоток и снимает зажимы.",
    price: "1700 ₽/час",
  },
  {
    id: 6,
    name: "Ароматерапевтический массаж",
    description: "С использованием эфирных масел. Глубокое расслабление и ароматерапия.",
    price: "1800 ₽/час",
  },
  {
    id: 7,
    name: "Тайский массаж",
    description: "Энергетическое восстановление по меридианам. Повышает гибкость и тонус.",
    price: "2200 ₽/час",
  },
];

function Massages() {
  return (
    <section className="py-16 px-6 bg-gradient-to-b from-white to-amber-50 min-h-screen mt-16">

      <div className="text-center max-w-3xl mx-auto mb-14">
        <h1 className="text-4xl sm:text-5xl font-light text-gray-800 mb-4">Массаж в бане</h1>
        <p className="text-lg text-gray-600 font-extralight">
          Профессиональный массаж в паре с баней усиливает эффект в разы. 
          Расслабьтесь полностью — тело и дух скажут вам спасибо.
        </p>
      </div>

      <div className="max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {massages.map((massage) => (
          <div
            key={massage.id}
            className="bg-white rounded-2xl shadow-lg overflow-hidden hover:shadow-xl transition-shadow duration-300 flex flex-col"
          >
          
            <div className="h-2 bg-gradient-to-r from-green-500 to-amber-500"></div>

            <div className="p-6 flex-1 flex flex-col">
              <h3 className="text-xl font-medium text-gray-800 mb-2">{massage.name}</h3>
              <p className="text-gray-600 text-sm leading-relaxed mb-4 flex-1">
                {massage.description}
              </p>

         
              <div className="mt-auto pt-2">
                <p className="text-lg font-semibold text-gray-800 mb-3">{massage.price}</p>
                <CustomButton
                  to="/booking"
                  text="Забронировать"
                  variant="green"
                  className="w-full py-2 text-sm hover:shadow-md transition"
                />
              </div>
            </div>
          </div>
        ))}
      </div>

      
    </section>
  );
}

export default Massages;
