// src/pages/Roadmap/Roadmap.jsx

import { useState, useEffect } from 'react';

function Roadmap() {
  const [expanded, setExpanded] = useState({});

  const toggleSection = (id) => {
    setExpanded((prev) => ({ ...prev, [id]: !prev[id] }));
  };

  // Автоматически открываем "В работе"
  useEffect(() => {
    const inProgress = roadmapData.find(phase => phase.status === 'in-progress');
    if (inProgress) {
      setExpanded({ [inProgress.id]: true });
    }
  }, []);

  const roadmapData = [
    {
      id: 1,
      title: '🔐 Авторизация и безопасность',
      status: 'done',
      color: 'bg-green-500',
      items: [
        'Реализована JWT-авторизация для админов',
        'Страница входа: /admin/login',
        'Проверка токена при каждом запросе',
        'Защита всех /admin/* маршрутов',
        'Токен хранится в localStorage',
      ],
    },
    {
      id: 2,
      title: '📩 Управление заявками с сайта',
      status: 'in-progress',
      color: 'bg-blue-500',
      items: [
        'Просмотр всех заявок из формы бронирования',
        'Разделение на вкладки: "Новые" и "Прочитанные"',
        'Заявки доступны только для просмотра (не редактируются)',
        'Возможность отметить заявку как "прочитанную"',
        'Модальное окно с деталями заявки',
        'Админ может вручную создать бронь на основе заявки',
      ],
    },
    {
      id: 3,
      title: '📅 Таблица бронирований (визуальный календарь)',
      status: 'planned',
      color: 'bg-gray-400',
      items: [
        'Фильтры: выбор бани и даты',
        'Отображение времени с шагом 30 минут (настраиваемо: 15/30/60 мин)',
        'Свободные слоты — светлый цвет, занятые — тёмный',
        'На занятых слотах отображается имя клиента',
        'Клик по слоту — детали или создание брони',
        'Проверка на пересечение броней (на бэкенде)',
      ],
    },
    {
      id: 4,
      title: '✍️ Ручное бронирование и редактирование',
      status: 'planned',
      color: 'bg-gray-400',
      items: [
        'Модальное окно создания брони: начало, конец, баня',
        'Выбор дополнительных услуг: веники, шашлык, массаж и др.',
        'Поля: имя, телефон, email, комментарий',
        'Автоматический расчёт стоимости (чек)',
        'Возможность редактирования и удаления брони',
        'Подробное окно просмотра с разбивкой по услугам и итогом',
      ],
    },
    {
      id: 5,
      title: '🚀 Будущие улучшения',
      status: 'planned',
      color: 'bg-purple-500',
      items: [
        'Настраиваемый шаг времени (15, 30, 60 минут)',
        'Экспорт расписания в Excel или PDF',
        'Интеграция с Telegram: уведомления о новых заявках',
        'WebSocket для автообновления таблицы без F5',
        'Адаптивный дизайн для мобильных устройств',
        'Поддержка нескольких админов с ролями',
        'Недельный и месячный вид календаря',
        'Интеграция с CRM или базой клиентов',
      ],
    },
  ];

  const getStatusText = (status) => {
    return {
      done: 'Готово',
      'in-progress': 'В работе',
      planned: 'Запланировано',
    }[status];
  };

  // Подсчёт прогресса
  const total = roadmapData.length;
  const completed = roadmapData.filter(p => p.status === 'done').length;
  const progress = total > 0 ? Math.round((completed / total) * 100) : 0;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-5xl mx-auto">
        {/* Заголовок */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-light text-gray-800 mb-3">Роадмап разработки</h1>
          <p className="text-lg text-gray-600 mb-6 max-w-3xl mx-auto">
            Полный план реализации админ-панели для управления баней. 
            Все этапы: от авторизации до ручного бронирования и будущих улучшений.
          </p>

          {/* Прогресс-бар */}
          <div className="bg-white rounded-xl p-4 shadow-md border border-gray-200 max-w-2xl mx-auto">
            <div className="flex justify-between text-sm text-gray-600 mb-2">
              <span>Прогресс: {completed} из {total} этапов</span>
              <span>{progress}%</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className="bg-gradient-to-r from-green-500 to-blue-500 h-2 rounded-full transition-all duration-500"
                style={{ width: `${progress}%` }}
              ></div>
            </div>
          </div>
        </div>

        {/* Этапы */}
        <div className="space-y-6">
          {roadmapData.map((phase) => (
            <div
              key={phase.id}
              className="bg-white rounded-2xl shadow-lg border border-gray-200 overflow-hidden hover:shadow-xl transition-shadow"
            >
              {/* Заголовок этапа */}
              <button
                onClick={() => toggleSection(phase.id)}
                className="w-full px-8 py-6 text-left flex items-center justify-between hover:bg-gray-50 transition-colors"
              >
                <div className="flex items-center space-x-4">
                  <span className={`w-4 h-4 rounded-full ${phase.color}`}></span>
                  <h2 className="text-xl font-medium text-gray-800">{phase.title}</h2>
                </div>
                <div className="flex items-center space-x-4">
                  <span
                    className={`text-sm px-3 py-1 rounded-full font-medium ${
                      phase.status === 'done'
                        ? 'bg-green-100 text-green-800'
                        : phase.status === 'in-progress'
                        ? 'bg-blue-100 text-blue-800'
                        : 'bg-gray-100 text-gray-800'
                    }`}
                  >
                    {getStatusText(phase.status)}
                  </span>
                  <svg
                    className={`w-5 h-5 text-gray-500 transition-transform duration-200 ${
                      expanded[phase.id] ? 'rotate-180' : ''
                    }`}
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
                  </svg>
                </div>
              </button>

              {/* Список задач */}
              {expanded[phase.id] && (
                <div className="px-8 pb-6 pt-2 border-t border-gray-100">
                  <ul className="space-y-2">
                    {phase.items.map((item, idx) => (
                      <li
                        key={idx}
                        className="flex items-start space-x-3 text-gray-700 leading-relaxed"
                      >
                        <span
                          className={`w-2 h-2 rounded-full mt-2 flex-shrink-0 ${
                            phase.status === 'done' ? 'bg-green-500' : 'bg-gray-400'
                          }`}
                        ></span>
                        <span className="text-sm">{item}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Легенда */}
        <div className="mt-12 bg-white rounded-xl p-6 shadow-md border border-gray-200">
          <h3 className="text-lg font-medium text-gray-800 mb-4">Статусы этапов</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-sm text-gray-600">
            <div className="flex items-center space-x-2">
              <span className="w-3 h-3 bg-green-500 rounded-full"></span>
              <span><strong>Готово</strong> — реализовано и работает</span>
            </div>
            <div className="flex items-center space-x-2">
              <span className="w-3 h-3 bg-blue-500 rounded-full"></span>
              <span><strong>В работе</strong> — активно разрабатывается</span>
            </div>
            <div className="flex items-center space-x-2">
              <span className="w-3 h-3 bg-gray-400 rounded-full"></span>
              <span><strong>Запланировано</strong> — будет реализовано позже</span>
            </div>
          </div>
        </div>

        {/* Подсказка */}
        <div className="mt-8 text-center text-sm text-gray-500">
          <p>Нажмите на этап, чтобы раскрыть детали. Текущий этап автоматически открыт.</p>
        </div>
      </div>
    </div>
  );
}

export default Roadmap;
