// src/pages/Admin/Reservations/ReservationsFilters.jsx

import { useState, useEffect } from 'react';

function ReservationsFilters({ onApply }) {
  const today = new Date().toISOString().split('T')[0];

  const [date, setDate] = useState(today);

  useEffect(() => {
    onApply({ date });
  }, [onApply]);

  const handleDateChange = (e) => {
    const newDate = e.target.value;
    setDate(newDate);
    onApply({ date: newDate }); // üëà –ü—Ä–∏–º–µ–Ω—è–µ–º —Å—Ä–∞–∑—É –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏
  };

  return (
    <div className="bg-white p-6 rounded-2xl shadow-md border border-gray-200 mb-8">
      <h2 className="text-xl font-semibold text-gray-800 mb-4">–§–∏–ª—å—Ç—Ä—ã</h2>
      <div className="grid grid-cols-1 md:grid-cols-1 gap-6">
        <div>
          <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-2">
            –í—ã–±–µ—Ä–∏—Ç–µ –¥–∞—Ç—É
          </label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={handleDateChange}
            className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
          />
        </div>
      </div>
    </div>
  );
}

export default ReservationsFilters;
