import { useState } from 'react';
import {
  useGetBathsQuery,
  useGetBathByIdQuery,
  useCreateBathMutation,
  useUpdateBathMutation,
  useDeleteBathMutation,
} from '../../../redux/slices/apiSlice';

function AdminBaths() {
  // Загружаем список бань
  const { data: baths, isLoading, error } = useGetBathsQuery();

  // Мутации
  const [createBath] = useCreateBathMutation();
  const [updateBath] = useUpdateBathMutation();
  const [deleteBath] = useDeleteBathMutation();

  // Состояния форм
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    title: '',
    cost: '',
    description: '',
    photo_urls: [],
    features: [],
  });

  const [isAddingNew, setIsAddingNew] = useState(false);
  const [newBath, setNewBath] = useState({
    name: '',
    title: '',
    cost: '',
    description: '',
    photo_urls: [],
    features: [],
  });

  const [deletingId, setDeletingId] = useState(null);

  // Открыть форму редактирования
  const handleEditClick = (bath) => {
    setEditingId(bath.bath_id);
    setEditForm({
      name: bath.name,
      title: bath.title,
      cost: bath.cost,
      description: bath.description || '',
      photo_urls: bath.images || [], // Используем images из преобразованного ответа
      features: bath.features || [],
    });
  };

  // Сохранить изменения
  const handleSaveEdit = async () => {
    if (!editForm.name || !editForm.title || !editForm.cost) return;

    try {
      await updateBath({
        bath_id: editingId,
        name: editForm.name,
        title: editForm.title,
        cost: Number(editForm.cost),
        description: editForm.description || null,
        photo_urls: editForm.photo_urls,
        features: editForm.features,
      }).unwrap();
      setEditingId(null);
      setEditForm({
        name: '',
        title: '',
        cost: '',
        description: '',
        photo_urls: [],
        features: [],
      });
    } catch (err) {
      console.error('Ошибка обновления:', err);
    }
  };

  // Отменить редактирование
  const handleCancelEdit = () => {
    setEditingId(null);
    setEditForm({
      name: '',
      title: '',
      cost: '',
      description: '',
      photo_urls: [],
      features: [],
    });
  };

  // Открыть форму добавления
  const handleAddClick = () => {
    setIsAddingNew(true);
    setNewBath({
      name: '',
      title: '',
      cost: '',
      description: '',
      photo_urls: [],
      features: [],
    });
  };

  // Добавить новую баню
  const handleAddBath = async () => {
    if (!newBath.name || !newBath.title || !newBath.cost) return;

    try {
      await createBath({
        name: newBath.name,
        title: newBath.title,
        cost: Number(newBath.cost),
        description: newBath.description || null,
        photo_urls: newBath.photo_urls,
        features: newBath.features,
      }).unwrap();
      setIsAddingNew(false);
      setNewBath({
        name: '',
        title: '',
        cost: '',
        description: '',
        photo_urls: [],
        features: [],
      });
    } catch (err) {
      console.error('Ошибка создания:', err);
    }
  };

  // Отменить добавление
  const handleCancelAdd = () => {
    setIsAddingNew(false);
    setNewBath({
      name: '',
      title: '',
      cost: '',
      description: '',
      photo_urls: [],
      features: [],
    });
  };

  // Удалить баню
  const handleDeleteClick = (id) => {
    setDeletingId(id);
  };

  const handleConfirmDelete = async () => {
    try {
      await deleteBath(deletingId).unwrap();
      setDeletingId(null);
    } catch (err) {
      console.error('Ошибка удаления:', err);
    }
  };

  const handleCancelDelete = () => {
    setDeletingId(null);
  };

  // Загрузка
  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 p-8 flex items-center justify-center">
        <div className="text-gray-600">Загрузка бань...</div>
      </div>
    );
  }

  // Ошибка
  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 p-8">
        <div className="max-w-7xl mx-auto text-red-500">Ошибка: {error.toString()}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-7xl mx-auto">
        {/* Заголовок */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-800">Бани</h1>
          <p className="text-gray-600 mt-2">Управление банями и их характеристиками</p>
        </div>

        {/* Кнопка "Добавить баню" */}
        <div className="mb-8 flex justify-end">
          <button
            onClick={handleAddClick}
            className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl font-medium shadow-md transition flex items-center space-x-2"
          >
            <span>➕</span>
            <span>Добавить баню</span>
          </button>
        </div>

        {/* Форма добавления новой бани */}
        {isAddingNew && (
          <div className="bg-white rounded-2xl shadow-lg p-6 mb-8 border border-gray-100">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Добавить новую баню</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              <input
                type="text"
                placeholder="Название бани"
                value={newBath.name}
                onChange={(e) => setNewBath({ ...newBath, name: e.target.value })}
                className="px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                autoFocus
              />
              <input
                type="text"
                placeholder="Заголовок (например: 'Кедровая парилка')"
                value={newBath.title}
                onChange={(e) => setNewBath({ ...newBath, title: e.target.value })}
                className="px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
              />
              <input
                type="number"
                placeholder="Цена (₽)"
                value={newBath.cost}
                onChange={(e) => setNewBath({ ...newBath, cost: e.target.value })}
                className="px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
              />
            </div>

            <div className="mb-6">
              <textarea
                placeholder="Описание бани"
                value={newBath.description}
                onChange={(e) => setNewBath({ ...newBath, description: e.target.value })}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500"
                rows="3"
              ></textarea>
            </div>

            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                URL-адреса фото (через запятую)
              </label>
              <input
                type="text"
                placeholder="https://example.com/photo1.jpg, https://example.com/photo2.jpg"
                value={newBath.photo_urls.join(', ')}
                onChange={(e) =>
                  setNewBath({
                    ...newBath,
                    photo_urls: e.target.value.split(',').map(url => url.trim()).filter(url => url),
                  })
                }
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500"
              />
              <p className="text-xs text-gray-500 mt-1">Разделяйте ссылки запятыми</p>
            </div>

            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Особенности (формат: ключ=значение, например: "температура=70°C")
              </label>
              <input
                type="text"
                placeholder="температура=70°C, объем=3м³"
                value={newBath.features.map(f => `${f.key}=${f.value}`).join(', ')}
                onChange={(e) =>
                  setNewBath({
                    ...newBath,
                    features: e.target.value
                      .split(',')
                      .map(pair => {
                        const [key, value] = pair.split('=').map(s => s.trim());
                        return key ? { key, value: value || '' } : {};
                      })
                      .filter(f => f.key),
                  })
                }
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500"
              />
              <p className="text-xs text-gray-500 mt-1">Формат: ключ=значение, разделите запятыми</p>
            </div>

            <div className="flex space-x-4">
              <button
                onClick={handleAddBath}
                className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl font-medium shadow transition"
              >
                Добавить
              </button>
              <button
                onClick={handleCancelAdd}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 px-6 py-3 rounded-xl font-medium shadow transition"
              >
                Отмена
              </button>
            </div>
          </div>
        )}

        {/* Таблица бань */}
        <div className="bg-white rounded-2xl shadow-lg overflow-hidden">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">Название</th>
                <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">Заголовок</th>
                <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">Цена</th>
                <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">Фото</th>
                <th className="px-6 py-4 text-right text-sm font-medium text-gray-700 uppercase tracking-wider">Действия</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {baths && baths.length > 0 ? (
                baths.map((bath) => (
                  <tr key={bath.bath_id} className="hover:bg-gray-50">
                    {editingId === bath.bath_id ? (
                      // Режим редактирования
                      <>
                        <td className="px-6 py-4">
                          <input
                            type="text"
                            value={editForm.name}
                            onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
                            className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                          />
                        </td>
                        <td className="px-6 py-4">
                          <input
                            type="text"
                            value={editForm.title}
                            onChange={(e) => setEditForm({ ...editForm, title: e.target.value })}
                            className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                          />
                        </td>
                        <td className="px-6 py-4">
                          <input
                            type="number"
                            value={editForm.cost}
                            onChange={(e) => setEditForm({ ...editForm, cost: e.target.value })}
                            className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                          />
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm text-gray-600">
                            {editForm.photo_urls.length} фото
                          </div>
                        </td>
                        <td className="px-6 py-4 text-right">
                          <div className="flex justify-end space-x-2">
                            <button
                              onClick={handleSaveEdit}
                              className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg text-sm font-medium shadow transition"
                            >
                              Сохранить
                            </button>
                            <button
                              onClick={handleCancelEdit}
                              className="bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded-lg text-sm font-medium shadow transition"
                            >
                              Отмена
                            </button>
                          </div>
                        </td>
                      </>
                    ) : (
                      // Режим просмотра
                      <>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {bath.name}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                          {bath.title}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                          {bath.cost} ₽
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                          {bath.images?.length || 0} фото
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                          <button
                            onClick={() => handleEditClick(bath)}
                            className="text-green-600 hover:text-green-900 mr-4 transition"
                          >
                            Редактировать
                          </button>
                          <button
                            onClick={() => handleDeleteClick(bath.bath_id)}
                            className="text-red-600 hover:text-red-900 transition"
                          >
                            Удалить
                          </button>
                        </td>
                      </>
                    )}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" className="px-6 py-12 text-center text-gray-500 text-lg">
                    Бани не найдены. Добавьте первую!
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        {/* Модальное окно подтверждения удаления */}
        {deletingId && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md">
              <h2 className="text-xl font-bold text-gray-800 mb-4">Подтвердите удаление</h2>
              <p className="text-gray-600 mb-6">
                Вы действительно хотите удалить баню? Это действие нельзя отменить.
              </p>

              <div className="flex justify-end space-x-4">
                <button
                  onClick={handleCancelDelete}
                  className="px-6 py-2 bg-gray-300 text-gray-800 rounded-xl font-medium hover:bg-gray-400 transition"
                >
                  Отмена
                </button>
                <button
                  onClick={handleConfirmDelete}
                  className="px-6 py-2 bg-red-600 text-white rounded-xl font-medium hover:bg-red-700 transition"
                >
                  Удалить
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminBaths;
