import bookingReducer from './slices/bookingSlice';
import { massagesApiSlice } from './slices/massagesApiSlice'; // ðŸ‘ˆ ÐÐ¾Ð²Ñ‹Ð¹ RTK Query slice Ð´Ð»Ñ Ð¼Ð°ÑÑÐ°Ð¶ÐµÐ¹

export const store = configureStore({
  reducer: {
    [apiSlice.reducerPath]: apiSlice.reducer, // ðŸ‘ˆ Ð¡Ñ‚Ð°Ñ€Ñ‹Ð¹ API-ÑÐ»Ð°Ð¹Ñ (Ð½Ðµ Ñ‚Ñ€Ð¾Ð³Ð°ÐµÐ¼!)
