import { Routes, Route, NavLink } from 'react-router-dom';
import AdminReservations from './AdminReservations/AdminReservations';
import AdminBookings from './AdminBookings/AdminBookings';

function Admin() {
  return (
    <div className="min-h-screen bg-gray-50 flex">
      {/* –°–∞–π–¥–±–∞—Ä */}
      <aside className="w-64 bg-white shadow-lg border-r border-gray-200 flex flex-col">
        <div className="p-6 border-b border-gray-200">
          <h1 className="text-2xl font-bold text-gray-800">–ê–¥–º–∏–Ω –ø–∞–Ω–µ–ª—å</h1>
          <p className="text-sm text-gray-500 mt-1">–ù–∏–∫–æ–ª–∞–µ–≤—Å–∫–∏–µ –±–∞–Ω–∏</p>
        </div>

        <nav className="flex-1 p-4 space-y-2">
          {/* –ó–∞–ø–∏—Å–∏ */}
          <NavLink
            to="/admin/reservations"
            className={({ isActive }) =>
              `flex items-center px-4 py-3 rounded-xl transition ${
                isActive
                  ? 'bg-green-100 text-green-800 border border-green-200 font-medium'
                  : 'text-gray-700 hover:bg-green-50 hover:text-green-700'
              }`
            }
          >
            üìÖ –ó–∞–ø–∏—Å–∏
          </NavLink>

          {/* –ó–∞—è–≤–∫–∏ */}
          <NavLink
            to="/admin/bookings"
            className={({ isActive }) =>
              `flex items-center px-4 py-3 rounded-xl transition ${
                isActive
                  ? 'bg-green-100 text-green-800 border border-green-200 font-medium'
                  : 'text-gray-700 hover:bg-green-50 hover:text-green-700'
              }`
            }
          >
            üì© –ó–∞—è–≤–∫–∏
          </NavLink>
        </nav>

        <div className="p-4 border-t border-gray-200">
          <button className="w-full text-left px-4 py-2 text-red-600 hover:bg-red-50 rounded-lg transition">
            –í—ã–π—Ç–∏
          </button>
        </div>
      </aside>

      {/* –û—Å–Ω–æ–≤–Ω–æ–µ —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ */}
      <main className="flex-1 p-8">
        <Routes>
          <Route path="/" element={<div>–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å. –ì–ª–∞–≤–Ω–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞ –∞–¥–º–∏–Ω –ø–∞–Ω–µ–ª–∏.</div>} />
          <Route path="/reservations" element={<AdminReservations />} />
          <Route path="/bookings" element={<AdminBookings />} />
        </Routes>
      </main>
    </div>
  );
}

export default Admin;
