// src/redux/store.js
import { configureStore } from '@reduxjs/toolkit';
import { apiSlice } from './slices/apiSlice';
import bookingReducer from './slices/bookingSlice';
import { massagesApiSlice } from './slices/massagesApiSlice'; // üëà –ù–æ–≤—ã–π RTK Query

export const store = configureStore({
  reducer: {
    [apiSlice.reducerPath]: apiSlice.reducer,
    booking: bookingReducer,
    // üëá –í–ê–ñ–ù–û: –ü–û–î–ö–õ–Æ–ß–ê–ï–ú RTK QUERY, –ù–û –ü–û–î –ò–ú–ï–ù–ï–ú 'massages'
    massages: massagesApiSlice.reducer, // ‚Üê –¢–ê–ö! –≠—Ç–æ –¥–µ–ª–∞–µ—Ç state.massages –¥–æ—Å—Ç—É–ø–Ω—ã–º!
  },
  middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware()
      .concat(apiSlice.middleware)
      .concat(massagesApiSlice.middleware), // üëà Middleware RTK Query –æ–±—è–∑–∞—Ç–µ–ª–µ–Ω!
});
