// src/pages/Admin/Reservations/AddBookingModal.jsx

import { createPortal } from 'react-dom';
import { useState } from 'react';

function AddBookingModal({ isOpen, onClose, onAdd }) {
  const [formData, setFormData] = useState({
    bath_id: '',
    start_datetime: '',
    end_datetime: '',
    client_name: '',
    client_phone: '',
    client_email: '',
    notes: '',
    services: [],
  });

  const mockBaths = [
    { bath_id: 1, name: "Кедровая баня" },
    { bath_id: 2, name: "Нефритовая баня" },
    { bath_id: 3, name: "Липовая баня" },
  ];

  const mockServices = [
    { service_id: 1, name: "Веники", cost: 500 },
    { service_id: 2, name: "Массаж", cost: 1500 },
    { service_id: 3, name: "Чай", cost: 300 },
  ];

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleServiceToggle = (service) => {
    setFormData(prev => {
      const exists = prev.services.find(s => s.service_id === service.service_id);
      if (exists) {
        return {
          ...prev,
          services: prev.services.filter(s => s.service_id !== service.service_id)
        };
      } else {
        return {
          ...prev,
          services: [...prev.services, { ...service, quantity: 1 }]
        };
      }
    });
  };

  const handleQuantityChange = (serviceId, delta) => {
    setFormData(prev => ({
      ...prev,
      services: prev.services.map(s =>
        s.service_id === serviceId
          ? { ...s, quantity: Math.max(1, s.quantity + delta) }
          : s
      )
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.bath_id || !formData.start_datetime || !formData.end_datetime || !formData.client_name) {
      alert('Заполните обязательные поля: баня, время, имя клиента');
      return;
    }

    if (new Date(formData.start_datetime) >= new Date(formData.end_datetime)) {
      alert('Время окончания должно быть позже начала');
      return;
    }

    const newBooking = {
      ...formData,
      reservation_id: Date.now(),
      bath_name: mockBaths.find(b => b.bath_id === parseInt(formData.bath_id))?.name || 'Неизвестно',
      total_cost: calculateTotal(formData, mockBaths),
    };

    onAdd(newBooking);
  };

  const calculateTotal = (data, baths) => {
    let total = 0;

    // Стоимость бани (условно 3000₽/час)
    const bathCostPerHour = 3000;
    const start = new Date(data.start_datetime);
    const end = new Date(data.end_datetime);
    const hours = (end - start) / (1000 * 60 * 60);
    total += Math.ceil(hours) * bathCostPerHour;

    // Стоимость услуг
    data.services.forEach(s => {
      total += s.cost * s.quantity;
    });

    return total;
  };

  if (!isOpen) return null;

  return createPortal(
    <div
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50"
      onClick={handleOverlayClick}
    >
      <div className="bg-white rounded-2xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6 border-b border-gray-200 bg-gradient-to-r from-gray-50 to-gray-100">
          <h2 className="text-xl font-semibold text-gray-800">Добавить бронь</h2>
          <button
            onClick={onClose}
            className="absolute top-6 right-6 text-gray-500 hover:text-gray-700 text-2xl"
          >
            ×
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {/* Баня */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Баня *
            </label>
            <select
              name="bath_id"
              value={formData.bath_id}
              onChange={handleChange}
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
              required
            >
              <option value="">Выберите баню</option>
              {mockBaths.map(bath => (
                <option key={bath.bath_id} value={bath.bath_id}>
                  {bath.name}
                </option>
              ))}
            </select>
          </div>

          {/* Время */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Начало *
              </label>
              <input
                type="datetime-local"
                name="start_datetime"
                value={formData.start_datetime}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Окончание *
              </label>
              <input
                type="datetime-local"
                name="end_datetime"
                value={formData.end_datetime}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
          </div>

          {/* Клиент */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Имя клиента *
            </label>
            <input
              type="text"
              name="client_name"
              value={formData.client_name}
              onChange={handleChange}
              placeholder="Иван Иванов"
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Телефон
              </label>
              <input
                type="tel"
                name="client_phone"
                value={formData.client_phone}
                onChange={handleChange}
                placeholder="+7 (999) 123-45-67"
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Email
              </label>
              <input
                type="email"
                name="client_email"
                value={formData.client_email}
                onChange={handleChange}
                placeholder="ivan@example.com"
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
              />
            </div>
          </div>

          {/* Услуги */}
          <div>
            <h3 className="text-lg font-medium text-gray-800 mb-3">Услуги</h3>
            <div className="space-y-3">
              {mockServices.map(service => {
                const selected = formData.services.find(s => s.service_id === service.service_id);
                return (
                  <div key={service.service_id} className="flex items-center justify-between p-3 border border-gray-200 rounded-lg">
                    <div className="flex items-center space-x-3">
                      <input
                        type="checkbox"
                        checked={!!selected}
                        onChange={() => handleServiceToggle(service)}
                        className="w-5 h-5"
                      />
                      <span>{service.name} — {service.cost} ₽</span>
                    </div>
                    {selected && (
                      <div className="flex items-center space-x-2">
                        <button
                          type="button"
                          onClick={() => handleQuantityChange(service.service_id, -1)}
                          className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center text-gray-700 hover:bg-gray-300"
                        >
                          −
                        </button>
                        <span className="w-8 text-center font-medium">{selected.quantity}</span>
                        <button
                          type="button"
                          onClick={() => handleQuantityChange(service.service_id, 1)}
                          className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center text-gray-700 hover:bg-gray-300"
                        >
                          +
                        </button>
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>

          {/* Комментарий */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Комментарий
            </label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              rows="3"
              placeholder="Пожелания клиента..."
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Итого */}
          <div className="bg-blue-50 p-4 rounded-xl border border-blue-200">
            <h4 className="font-medium text-blue-800 mb-2">Расчёт стоимости</h4>
            <div className="text-sm text-blue-700 space-y-1">
              <div>Стоимость бани: ~{Math.ceil((new Date(formData.end_datetime) - new Date(formData.start_datetime)) / (1000 * 60 * 60)) * 3000} ₽</div>
              <div>Услуги: {formData.services.reduce((sum, s) => sum + s.cost * s.quantity, 0)} ₽</div>
              <div className="font-bold">Итого: {calculateTotal(formData, mockBaths)} ₽</div>
            </div>
          </div>

          {/* Кнопки */}
          <div className="pt-4 border-t border-gray-200 flex gap-3">
            <button
              type="submit"
              className="flex-1 bg-green-600 text-white py-3 px-6 rounded-xl font-medium hover:bg-green-700 transition"
            >
              Создать бронь
            </button>
            <button
              type="button"
              onClick={onClose}
              className="flex-1 bg-gray-200 text-gray-800 py-3 px-6 rounded-xl font-medium hover:bg-gray-300 transition"
            >
              Отмена
            </button>
          </div>
        </form>
      </div>
    </div>,
    document.body
  );
}

export default AddBookingModal;
