import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// ðŸ” Ð‘ÐµÐ·Ð¾Ð¿Ð°ÑÐ½Ð¾Ðµ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ VITE_API_URL
const getApiUrl = () => {
  // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼, Ñ‡Ñ‚Ð¾ import.meta Ð¸ import.meta.env Ð´Ð¾ÑÑ‚ÑƒÐ¿Ð½Ñ‹
  if (typeof import.meta !== 'undefined' && import.meta.env) {
    // return import.meta.env.VITE_API_URL || 'http://127.0.0.1:8000';
    return import.meta.env.VITE_API_URL || 'http://192.168.0.100:3001';
  }
  // Ð•ÑÐ»Ð¸ import.meta Ð½ÐµÐ´Ð¾ÑÑ‚ÑƒÐ¿ÐµÐ½ (Ñ€ÐµÐ´ÐºÐ¸Ð¹ ÑÐ»ÑƒÑ‡Ð°Ð¹, Ð½Ð¾ Ð±Ñ‹Ð²Ð°ÐµÑ‚ Ð¿Ñ€Ð¸ HMR Ð¸Ð»Ð¸ Ð¾ÑˆÐ¸Ð±ÐºÐ°Ñ… ÑÐ±Ð¾Ñ€ÐºÐ¸)
  // return 'http://127.0.0.1:8000';
  return 'http://192.168.0.100:3001';
};

const API_BASE_URL = getApiUrl();

export const createBooking = createAsyncThunk(
  'booking/createBooking',
  async (bookingData, { rejectWithValue }) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/api/bookings/`, bookingData);
      return response.data;
    } catch (error) {
      // Ð›Ð¾Ð³Ð¸Ñ€ÑƒÐµÐ¼ Ð¾ÑˆÐ¸Ð±ÐºÑƒ Ð´Ð»Ñ Ð¾Ñ‚Ð»Ð°Ð´ÐºÐ¸
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐµ Ð±Ñ€Ð¾Ð½Ð¸:', error.response?.data || error.message);
      return rejectWithValue(
        error.response?.data?.detail || 
        error.message || 
        'ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ Ð±Ñ€Ð¾Ð½ÑŒ'
      );
    }
  }
);

const bookingSlice = createSlice({
  name: 'booking',
  initialState: {
    loading: false,
    error: null,
    success: false,
  },
  reducers: {
    resetStatus: (state) => {
      state.loading = false;
      state.success = false;
      state.error = null;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(createBooking.pending, (state) => {
        state.loading = true;
        state.error = null;
        state.success = false;
      })
      .addCase(createBooking.fulfilled, (state) => {
        state.loading = false;
        state.success = true;
      })
      .addCase(createBooking.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export const { resetStatus } = bookingSlice.actions;
export default bookingSlice.reducer;
