import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import CustomButton from '../../../../components/UI/CustomButton/CustomButton';

import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

const bathsList = [
    [1, 'Кедровая баня', 'Русская кедровая баня на берёзовых дровах', '/img/Kedrovaya-1.png', '/kedrovaya'],
    [2, 'Нефритовая баня', 'Русская нефритовая баня на берёзовых дровах', '/img/bg-home.png', '/nefritovaya']
];

function SliderBaths() {
    return (
        <div>
            <Swiper
                modules={[Navigation, Pagination, Autoplay]}
                spaceBetween={0}
                slidesPerView={1}
                navigation
                pagination={{ clickable: true }}
                autoplay={{
                    delay: 4000,
                    disableOnInteraction: false,
                }}
                loop
                speed={800}
                className="my-swiper"
            >
                {bathsList.map(([id, name, title, img, path]) => (
                    <SwiperSlide key={id}>
                        <div className='flex justify-center items-center gap-10'>
                            <div className='w-640 h-600'>
                                <img src={img} alt={name} className='w-640 h-600' />
                            </div>
                            <div className='flex flex-col '>
                                <div className='gap-5 flex  flex-col text-white'>
                                    <h1 className='text-7xl font-light'>{name}</h1>
                                    <p className='text-2xl font-extralight max-w-md'>{title}</p>
                                </div>
                                <div className='flex gap-10'>
                                    <CustomButton to={path} text='Подробнее'/>
                                    <CustomButton to='/baths' variant='green' text='Все бани'/>
                                </div>
                            </div>
                        </div>
                    </SwiperSlide>
                ))}
            </Swiper>
        </div>
    );
}

export default SliderBaths;
