// src/pages/Admin/AdminReservations/AdminReservations.jsx

import { useState, useMemo } from 'react';
import ReservationsFilters from '../Reservations/ReservationsFilters';
import BookingDetailsModal from '../Reservations/BookingDetailsModal';
import AddBookingModal from '../Reservations/AddBookingModal';
import { useGetBathsQuery } from '../../../redux/slices/apiSlice';

// Моковые данные
const mockReservations = [
  {
    reservation_id: 1,
    bath_id: 1,
    bath_name: "Кедровая баня",
    start_datetime: "2025-04-05T10:00:00",
    end_datetime: "2025-04-05T12:00:00",
    client_name: "Иван Иванов",
    client_phone: "+7 (999) 123-45-67",
    client_email: "ivan@example.com",
    notes: "Хочу веники",
    total_cost: 6000,
    services: [
      { service_id: 1, name: "Веники", cost: 500, quantity: 2 }
    ]
  },
  {
    reservation_id: 2,
    bath_id: 1,
    bath_name: "Кедровая баня",
    start_datetime: "2025-04-05T14:00:00",
    end_datetime: "2025-04-05T15:30:00",
    client_name: "Мария Петрова",
    client_phone: "+7 (987) 654-32-10",
    client_email: "maria@example.com",
    notes: "",
    total_cost: 4500,
    services: []
  },
  {
    reservation_id: 3,
    bath_id: 2,
    bath_name: "Нефритовая баня",
    start_datetime: "2025-04-05T11:00:00",
    end_datetime: "2025-04-05T13:00:00",
    client_name: "Алексей Смирнов",
    client_phone: "+7 (912) 345-67-89",
    notes: "Тихий клиент",
    total_cost: 8000,
    services: []
  },
  {
    reservation_id: 4,
    bath_id: 3,
    bath_name: "Липовая баня",
    start_datetime: "2025-04-05T16:00:00",
    end_datetime: "2025-04-05T18:00:00",
    client_name: "Ольга Кузнецова",
    client_phone: "+7 (921) 234-56-78",
    notes: "",
    total_cost: 5000,
    services: []
  }
];

function AdminReservations() {
  const today = new Date().toISOString().split('T')[0];
  const [filters, setFilters] = useState({ date: today });
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);

  const { data: baths = [], isLoading: isLoadingBaths } = useGetBathsQuery();

  // 👇 Генерация временных слотов — всегда
  const timeSlots = useMemo(() => {
    if (!filters.date) return [];
    const slots = [];
    const startHour = 9;
    const endHour = 24;

    for (let hour = startHour; hour < endHour; hour++) {
      for (let minute of [0, 30]) {
        const timeStr = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
        const dateTime = new Date(`${filters.date}T${timeStr}:00`);
        slots.push({
          time: timeStr,
          start: dateTime,
          end: new Date(dateTime.getTime() + 30 * 60000),
        });
      }
    }
    return slots;
  }, [filters.date]);

  // 👇 ВСЕГДА отображаем все бани, даже если у них нет броней
  const bathsToDisplay = useMemo(() => {
    if (!baths || baths.length === 0) return [];

    return baths.map(bath => {
      // ✅ Исправлено: mockReservations вместо reservations
      const bookings = mockReservations.filter(res => {
        const resDate = new Date(res.start_datetime).toISOString().split('T')[0];
        return res.bath_id === bath.bath_id && resDate === filters.date;
      });

      return {
        bath_id: bath.bath_id,
        bathName: bath.name,
        bookings: bookings,
      };
    });
  }, [filters.date, baths]);

  const handleApply = (filters) => {
    setFilters(filters);
  };

  const handleViewBooking = (booking) => {
    setSelectedBooking(booking);
  };

  const handleEditBooking = (booking) => {
    console.log('Редактировать:', booking);
    setSelectedBooking(null);
  };

  const handleDeleteBooking = (id) => {
    if (window.confirm('Вы уверены, что хотите удалить эту бронь?')) {
      setSelectedBooking(null);
    }
  };

  const handleCloseModal = () => {
    setSelectedBooking(null);
  };

  const handleAddBooking = (newBooking) => {
    newBooking.reservation_id = newBooking.reservation_id || Date.now();
    mockReservations.push(newBooking);
    setFilters(prev => ({ ...prev }));
    setIsAddModalOpen(false);
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });
  };

  // ✅ Условный рендер ТОЛЬКО после всех хуков
  if (isLoadingBaths) {
    return (
      <div className="flex items-center justify-center py-16">
        <div className="text-gray-500">Загрузка списка бань...</div>
      </div>
    );
  }

  return (
    <div>
      <h2 className="text-2xl font-semibold text-gray-800 mb-6">
        Записи на {new Date(filters.date).toLocaleDateString('ru-RU', {
          day: 'numeric',
          month: 'long',
          year: 'numeric',
        })}
      </h2>

      <ReservationsFilters
        onApply={handleApply}
        onAddBooking={() => setIsAddModalOpen(true)}
      />

      {isLoadingBaths ? (
        <div className="flex items-center justify-center py-16">
          <div className="text-gray-500">Загрузка списка бань...</div>
        </div>
      ) : (
        <div className="overflow-x-auto pb-4 -mx-4 px-4">
          <div className="inline-flex space-x-6">
            {/* Колонка времени */}
            <div className="flex-shrink-0 w-32 bg-gray-50 p-4 border-r border-gray-200">
              <div className="text-sm font-medium text-gray-700 mb-2">ВРЕМЯ</div>
              {timeSlots.map((slot, idx) => (
                <div key={idx} className="text-sm text-gray-600 py-2">
                  {formatTime(slot.start)}
                </div>
              ))}
            </div>

            {/* Колонки с банями */}
            {bathsToDisplay.map((bath, idx) => {
              const hasBookings = bath.bookings.length > 0;

              return (
                <div key={bath.bath_id} className="flex-shrink-0 w-96">
                  <div className="bg-white rounded-xl shadow-md border border-gray-200 overflow-hidden">
                    <div className="p-4 border-b border-gray-200 bg-gray-50">
                      <h3 className="text-lg font-semibold text-gray-800">{bath.bathName}</h3>
                    </div>

                    {hasBookings ? (
                      <div className="overflow-x-auto">
                        <table className="w-full table-fixed border-separate border-spacing-0">
                          <thead className="bg-gray-50">
                            <tr>
                              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                Статус
                              </th>
                            </tr>
                          </thead>
                          <tbody>
                            {timeSlots.map((slot, idx) => {
                              const normalizedBookings = bath.bookings.map(booking => ({
                                ...booking,
                                start: new Date(booking.start_datetime),
                                end: new Date(booking.end_datetime),
                              }));

                              const overlapping = normalizedBookings.find((res) =>
                                res.start < slot.end && res.end > slot.start
                              );

                              return (
                                <tr key={idx} className="border-b border-gray-100 hover:bg-gray-25 transition-colors">
                                  <td className="px-6 py-0 h-14 relative bg-gray-50">
                                    {overlapping ? (
                                      <div
                                        className="absolute inset-y-0 left-0 right-0 bg-red-50 border border-red-200 text-red-800 text-sm font-medium rounded-lg px-3 flex items-center justify-center cursor-pointer hover:shadow transition-all duration-200 group"
                                        style={{
                                          top: `${((overlapping.start - slot.start) / (1000 * 60)) / 30 * 100}%`,
                                          height: `${((overlapping.end - overlapping.start) / (1000 * 60)) / 30 * 100}%`,
                                        }}
                                        title={`${overlapping.client_name} • ${formatTime(overlapping.start)} – ${formatTime(overlapping.end)}`}
                                        onClick={() => handleViewBooking(overlapping)}
                                      >
                                        <div className="w-1 bg-red-400 rounded-full absolute left-0 top-1 bottom-1 group-hover:w-2 transition-all duration-200"></div>
                                        <span className="truncate max-w-full text-center leading-tight font-medium">
                                          {overlapping.client_name}
                                        </span>
                                      </div>
                                    ) : (
                                      <div className="absolute inset-0 flex items-center justify-center text-green-600 text-sm font-medium bg-green-50 rounded-lg border border-green-200">
                                        Свободно
                                      </div>
                                    )}
                                  </td>
                                </tr>
                              );
                            })}
                          </tbody>
                        </table>
                      </div>
                    ) : (
                      <div className="h-full flex items-center justify-center p-8">
                        <div className="text-center text-gray-500 py-10 bg-gray-50 rounded-lg w-full">
                          <svg className="mx-auto h-12 w-12 text-gray-400 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                          </svg>
                          <p className="text-lg font-medium">Нет записей</p>
                          <p className="text-sm mt-1">На эту дату для бани "{bath.bathName}" бронирований нет.</p>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}

      <BookingDetailsModal
        booking={selectedBooking}
        onClose={handleCloseModal}
        onEdit={handleEditBooking}
        onDelete={handleDeleteBooking}
      />

      <AddBookingModal
        isOpen={isAddModalOpen}
        onClose={() => setIsAddModalOpen(false)}
        onAdd={handleAddBooking}
      />
    </div>
  );
}

export default AdminReservations;
