// src/App.jsx

import { BrowserRouter, Routes, Route, useLocation } from 'react-router-dom';
import Header from './components/Header/Header';
import Home from './pages/Home/Home';
import Booking from './pages/Booking/Booking';
import Baths from './pages/Baths/Baths';
import Contacts from './pages/Contacts/Contacts';
import Massages from './pages/Massages/Massages';
import BathsCard from './pages/Baths/BathsCard/BathsCard';
import AdminLogin from './pages/Admin/AdminLogin/AdminLogin';

// Админ-компоненты
import Admin from './pages/Admin/Admin';
import AdminReservations from './pages/Admin/AdminReservations/AdminReservations';
import AdminBookings from './pages/Admin/AdminBookings/AdminBookings';

// Компонент для скрытия Header
function AppWithLayout() {
  const location = useLocation();
  const hideHeader = location.pathname.startsWith('/admin');

  return (
    <>
      {!hideHeader && <Header />}
      <Routes>
        {/* Основные страницы */}
        <Route path="/" element={<Home />} />
        <Route path="/booking" element={<Booking />} />
        <Route path="/baths" element={<Baths />} />
        <Route path="/baths/:id" element={<BathsCard />} />
        <Route path="/contact" element={<Contacts />} />
        <Route path="/massages" element={<Massages />} />

        {/* Админ-панель */}
        <Route path="/admin" element={<Admin />}>
          <Route index element={<div>Добро пожаловать. Главная страница админ панели.</div>} />
          <Route path="reservations" element={<AdminReservations />} />
          <Route path="bookings" element={<AdminBookings />} />
        </Route>

        {/* Страница входа */}
        <Route path="/admin/login" element={<AdminLogin />} />
      </Routes>
    </>
  );
}

function App() {
  return (
    <BrowserRouter>
      <AppWithLayout />
    </BrowserRouter>
  );
}

export default App;
