// src/redux/store.js
import { configureStore } from '@reduxjs/toolkit';
import { apiSlice } from './slices/apiSlice'; // 👈 ВАЖНО: ИМПОРТИРУЕМ ТОЛЬКО ОДИН РАЗ!
import bookingReducer from './slices/bookingSlice';

export const store = configureStore({
  reducer: {
    [apiSlice.reducerPath]: apiSlice.reducer, // 👈 ЭТО ДОЛЖНО БЫТЬ!
    booking: bookingReducer,
  },
  middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware().concat(apiSlice.middleware), // 👈 И ЭТО!
});
