// src/pages/Admin/Reservations/BookingDetailsModal.jsx

import { useState } from 'react';
import { useGetBathsQuery } from '../../../redux/slices/apiSlice'; // üëà –î–æ–±–∞–≤–ª—è–µ–º —Ö—É–∫
import AddBookingModal from './AddBookingModal';

function BookingDetailsModal({ booking, onClose, onEdit, onDelete }) {
  const [isEditing, setIsEditing] = useState(false);
  const { data: baths = [], isLoading: isLoadingBaths } = useGetBathsQuery(); // üëà –ü–æ–ª—É—á–∞–µ–º —Å–ø–∏—Å–æ–∫ –±–∞–Ω—å

  if (!booking) return null;

  // üëá –ù–∞—Ö–æ–¥–∏–º –±–∞–Ω—é –ø–æ bath_id
  const bath = baths.find(b => b.bath_id === booking.bath_id);
  const bathName = bath?.name || '–ë–∞–Ω—è –Ω–µ –Ω–∞–π–¥–µ–Ω–∞';

  if (isEditing) {
    return (
      <AddBookingModal
        isOpen={true}
        onClose={onClose}
        onEdit={onEdit}
        booking={booking}
      />
    );
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-2xl shadow-2xl max-w-lg w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6 border-b border-gray-200 bg-gradient-to-r from-gray-50 to-gray-100">
          <h2 className="text-xl font-semibold text-gray-800">–î–µ—Ç–∞–ª–∏ –±—Ä–æ–Ω–∏</h2>
          <button
            onClick={onClose}
            className="absolute top-6 right-6 text-gray-500 hover:text-gray-700 text-2xl"
          >
            √ó
          </button>
        </div>

        <div className="p-6 space-y-4 text-sm">
          <div>
            <strong>–ö–ª–∏–µ–Ω—Ç:</strong> {booking.client_name}
          </div>
          <div>
            <strong>–¢–µ–ª–µ—Ñ–æ–Ω:</strong> {booking.client_phone}
          </div>
          {booking.client_email && (
            <div>
              <strong>Email:</strong> {booking.client_email}
            </div>
          )}
          <div>
            <strong>–ë–∞–Ω—è:</strong> {bathName} {/* üëà –û—Ç–æ–±—Ä–∞–∂–∞–µ–º –Ω–∞–π–¥–µ–Ω–Ω–æ–µ –Ω–∞–∑–≤–∞–Ω–∏–µ */}
          </div>
          <div>
            <strong>–í—Ä–µ–º—è:</strong>{' '}
            {new Date(booking.start_datetime).toLocaleString('ru-RU')} ‚Äî{' '}
            {new Date(booking.end_datetime).toLocaleString('ru-RU')}
          </div>
          {booking.notes && (
            <div>
              <strong>–ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π:</strong> {booking.notes}
            </div>
          )}
        </div>

        <div className="p-6 border-t border-gray-200 flex flex-col sm:flex-row gap-3">
          <button
            onClick={() => setIsEditing(true)}
            className="flex-1 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition font-medium"
          >
            –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
          </button>
          <button
            onClick={() => {
              if (window.confirm('–£–¥–∞–ª–∏—Ç—å –±—Ä–æ–Ω—å?')) {
                onDelete(booking.reservation_id);
              }
            }}
            className="flex-1 bg-red-100 text-red-700 py-2 px-4 rounded-lg hover:bg-red-200 transition font-medium"
          >
            –£–¥–∞–ª–∏—Ç—å
          </button>
          <button
            onClick={onClose}
            className="flex-1 bg-gray-200 text-gray-800 py-2 px-4 rounded-lg hover:bg-gray-300 transition font-medium"
          >
            –ó–∞–∫—Ä—ã—Ç—å
          </button>
        </div>
      </div>
    </div>
  );
}

export default BookingDetailsModal;
