// src/pages/Booking/Booking.jsx
import { useState } from 'react';
import { useGetBathsQuery, useCreateBookingMutation } from '../../redux/slices/apiSlice';
import CustomButton from '../../components/UI/CustomButton/CustomButton';

function Booking() {
  // –ó–∞–≥—Ä—É–∂–∞–µ–º —Å–ø–∏—Å–æ–∫ –±–∞–Ω—å
  const { data: baths = [], isLoading: isLoadingBaths, error: fetchError } = useGetBathsQuery();

  // –ú—É—Ç–∞—Ü–∏—è –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è –±—Ä–æ–Ω–∏
  const [createBooking, { isLoading: isSubmitting }] = useCreateBookingMutation();

  // –°–æ—Å—Ç–æ—è–Ω–∏–µ —Ñ–æ—Ä–º—ã
  const [formData, setFormData] = useState({
    date: '',
    hours: 3,
    bath_id: '',
    guests: 1,
    name: '',
    phone: '',
    email: '',
    notes: '',
  });

  // –°–æ–æ–±—â–µ–Ω–∏—è
  const [success, setSuccess] = useState('');
  const [formError, setFormError] = useState(''); // ‚Üê –∏–∑–º–µ–Ω–∏–ª–∏ –∏–º—è

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    setSuccess('');
    setFormError('');

    if (!formData.bath_id || !formData.date || !formData.name || !formData.phone) {
      setFormError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –∑–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è.');
      return;
    }

    // üîç –î–æ–±–∞–≤—å —ç—Ç–æ:
    console.log('–§–æ—Ä–º–∞:', formData);

    const bookingData = {
      bath_id: Number(formData.bath_id),
      date: formData.date,
      duration_hours: Number(formData.hours),
      guests: Number(formData.guests),
      name: formData.name.trim(),
      phone: formData.phone.trim(),
      email: formData.email?.trim() || null,
      notes: formData.notes.trim() || null,
    };

    // üîç –î–æ–±–∞–≤—å —ç—Ç–æ:
