import { useEffect, useState } from 'react';

function ReservationsTable({ reservations = [], date, bathId }) {
  const [timeSlots, setTimeSlots] = useState([]);


  useEffect(() => {
    const slots = [];
    const startHour = 9;
    const endHour = 24;

    for (let hour = startHour; hour < endHour; hour++) {
      for (let minute of [0, 30]) {
        const time = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
        slots.push(time);
      }
    }
    setTimeSlots(slots);
  }, []);

  
  const formatTime = (isoString) => {
    return new Date(isoString).toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });
  };


  const isOccupied = (time) => {
    return reservations.some((res) => {
      const start = formatTime(res.start_datetime);
      const end = formatTime(res.end_datetime);
      return time >= start && time < end;
    });
  };


  const getBookingInfo = (time) => {
    return reservations.find((res) => {
      const start = formatTime(res.start_datetime);
      const end = formatTime(res.end_datetime);
      return time >= start && time < end;
    });
  };

  if (!date || !bathId) {
    return (
      <div className="text-center py-12 text-gray-500">
        <p>Выберите баню и дату, чтобы посмотреть расписание</p>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-2xl shadow-md border border-gray-200 overflow-hidden">
      <div className="p-6 border-b border-gray-200">
        <h2 className="text-xl font-semibold text-gray-800">
          Расписание на {new Date(date).toLocaleDateString('ru-RU')}
        </h2>
      </div>

      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="bg-gray-50 border-b border-gray-200">
              <th className="text-left py-3 px-6 text-sm font-medium text-gray-700 w-32">Время</th>
              <th className="text-left py-3 px-6 text-sm font-medium text-gray-700">Статус</th>
            </tr>
          </thead>
          <tbody>
            {timeSlots.map((time, idx) => {
              const booking = getBookingInfo(time);
              const occupied = isOccupied(time);

              return (
                <tr key={idx} className="border-b border-gray-100 hover:bg-gray-50">
                  <td className="py-4 px-6 text-sm text-gray-800 font-medium">{time}</td>
                  <td className="py-4 px-6">
                    {occupied && booking ? (
                      <div className="bg-red-100 text-red-800 px-4 py-2 rounded-lg inline-block text-sm font-medium">
                        {booking.client_name}
                      </div>
                    ) : (
                      <div className="bg-green-100 text-green-800 px-4 py-2 rounded-lg inline-block text-sm">
                        Свободно
                      </div>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default ReservationsTable;
