// src/pages/Kitchen/Kitchen.jsx
import { useState } from 'react';

function Kitchen() {
  // Состояние активной вкладки
  const [activeTab, setActiveTab] = useState('drinks');

  // Данные по категориям (в реальном проекте — это будет fetch из API)
  const menuItems = {
    drinks: [
      { id: 1, name: 'Морс ягодный', price: 120 },
      { id: 2, name: 'Лимонад с мятой', price: 150 },
      { id: 3, name: 'Зелёный чай с имбирём', price: 90 },
      { id: 4, name: 'Квас домашний', price: 80 },
    ],
    snacks: [
      { id: 5, name: 'Чипсы с укропом', price: 100 },
      { id: 6, name: 'Солёные огурцы', price: 70 },
      { id: 7, name: 'Орехи с солью', price: 130 },
      { id: 8, name: 'Сушёные фрукты', price: 110 },
    ],
    hotDishes: [
      { id: 9, name: 'Борщ с пампушками', price: 250 },
      { id: 10, name: 'Жареный картофель с грибами', price: 180 },
      { id: 11, name: 'Курица в горшочке', price: 320 },
      { id: 12, name: 'Рыба под маринадом', price: 280 },
    ],
  };

  // Тексты для вкладок
  const tabs = [
    { id: 'drinks', label: 'Напитки' },
    { id: 'snacks', label: 'Закуски' },
    { id: 'hotDishes', label: 'Горячие блюда' },
  ];

  const currentItems = menuItems[activeTab] || [];

  return (
    <section className="py-16 px-6 bg-gradient-to-b from-white to-amber-50 min-h-screen mt-28">
      <div className="text-center max-w-3xl mx-auto mb-12">
        <h1 className="text-4xl sm:text-5xl font-light text-gray-800 mb-4">Кухня бани</h1>
        <p className="text-lg text-gray-600 font-extralight">
          После парилки — вкусно и тепло. Подберите идеальное сочетание для полного расслабления.
        </p>
      </div>

      {/* ВКЛАДКИ — мягкие, с акцентом на текст */}
      <div className="max-w-5xl mx-auto mb-12 flex justify-center">
        <div className="flex bg-gray-50 rounded-full p-1 shadow-sm">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`px-7 py-3 rounded-full text-sm font-medium transition-all duration-300 focus:outline-none ${
                activeTab === tab.id
                  ? 'bg-gradient-to-r bg-green-400 text-white shadow-md'
                  : 'text-gray-700 hover:text-gray-900 hover:bg-gray-100'
              }`}
            >
              {tab.label}
            </button>
          ))}
        </div>
      </div>

      {/* ТОВАРЫ — чистые карточки, без кнопок */}
      <div className="max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8 px-4">
        {currentItems.length > 0 ? (
          currentItems.map((item) => (
            <div
              key={item.id}
              className="bg-white rounded-2xl shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300 flex flex-col"
            >
              {/* Тонкая декоративная полоска — вместо градиента */}
              <div className="h-1 bg-green-400"></div>

              <div className="p-6 flex-1 flex flex-col items-center text-center">
                <h3 className="text-xl font-medium text-gray-800 mb-1">{item.name}</h3>
                <p className="text-gray-500 text-sm leading-relaxed mb-4">
                  Идеально дополняет атмосферу бани
                </p>

                <div className="mt-auto pt-4">
                  <p className="text-2xl font-bold text-gray-800">{item.price} ₽</p>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="col-span-4 text-center text-gray-500">Товары не найдены</div>
        )}
      </div>
    </section>
  );
}

export default Kitchen;
