import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import { NavLink } from 'react-router-dom';


const bathsList = [
    ['Кедровая баня', 'Русская кедровая баня на берёзовых дровах', '/img/Kedrovaya-1.png', '/kedrovaya'],
    ['Нефритовая баня', 'Русская нефритовая баня на берёзовых дровах', '/img/bg-home.png', '/nefritovaya']

]

function Slider() {
    return (
        <div >
            <Swiper
                modules={[Navigation, Pagination, Autoplay]}
                spaceBetween={0}
                slidesPerView={1}
                navigation
                pagination={{ clickable: true }}
                autoplay={{
                    delay: 4000,
                    disableOnInteraction: false,
                }}
                loop
                speed={800}
                className="my-swiper"
            >
                {bathsList.map((name, title, img, path) => (
                    <SwiperSlide key={name}>
                        <div>
                            <div>
                                <img src={img} alt="" />
                            </div>
                            <div>
                                <div>
                                    <h1>{name}</h1>
                                    <p>{title}</p>
                                </div>
                                <div>
                                    кнопка
                                </div>
                            </div>
                        </div>
                    </SwiperSlide>
                ))}
            </Swiper>
        </div>
    );
}

export default Slider;
