// src/pages/Massages/Massages.jsx
import { useGetMassagesQuery } from '../../redux/slices/apiSlice'; // üëà –ò–ó–ú–ï–ù–Å–ù –ü–£–¢–¨!
import CustomButton from '../../components/UI/CustomButton/CustomButton';

function Massages() {
  const { data: massages, isLoading, error } = useGetMassagesQuery();

  if (isLoading) {
    return (
      <section className="py-16 px-6 bg-gradient-to-b from-white to-amber-50 min-h-screen mt-28">
        <div className="text-center text-gray-600">–ó–∞–≥—Ä—É–∑–∫–∞ –º–∞—Å—Å–∞–∂–µ–π...</div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="py-16 px-6 bg-gradient-to-b from-white to-amber-50 min-h-screen mt-28">
        <div className="text-center text-red-500">–û—à–∏–±–∫–∞: {error.toString()}</div>
      </section>
    );
  }

  return (
    <section className="py-16 px-6 bg-gradient-to-b from-white to-amber-50 min-h-screen mt-28">
      <div className="text-center max-w-3xl mx-auto mb-14">
        <h1 className="text-4xl sm:text-5xl font-light text-gray-800 mb-4">–ú–∞—Å—Å–∞–∂ –≤ –±–∞–Ω–µ</h1>
        <p className="text-lg text-gray-600 font-extralight">
          –ü—Ä–æ—Ñ–µ—Å—Å–∏–æ–Ω–∞–ª—å–Ω—ã–π –º–∞—Å—Å–∞–∂ –≤ –ø–∞—Ä–µ —Å –±–∞–Ω–µ–π —É—Å–∏–ª–∏–≤–∞–µ—Ç —ç—Ñ—Ñ–µ–∫—Ç –≤ —Ä–∞–∑—ã. 
          –†–∞—Å—Å–ª–∞–±—å—Ç–µ—Å—å –ø–æ–ª–Ω–æ—Å—Ç—å—é ‚Äî —Ç–µ–ª–æ –∏ –¥—É—Ö —Å–∫–∞–∂—É—Ç –≤–∞–º —Å–ø–∞—Å–∏–±–æ.
        </p>
      </div>

      <div className="max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {massages && massages.length > 0 ? (
          massages.map((massage) => (
            <div
              key={massage.massage_id}
              className="bg-white rounded-2xl shadow-lg overflow-hidden hover:shadow-xl transition-shadow duration-300 flex flex-col"
            >
              <div className="h-2 bg-gradient-to-r from-green-500 to-amber-500"></div>

              <div className="p-6 flex-1 flex flex-col">
                <h3 className="text-xl font-medium text-gray-800 mb-2">{massage.name}</h3>
                <p className="text-gray-600 text-sm leading-relaxed mb-4 flex-1">
                  {massage.description}
                </p>

                <div className="mt-auto pt-2">
                  <p className="text-lg font-semibold text-gray-800 mb-3">{massage.price}</p> {/* üëà –£–∂–µ —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–æ */}
                  <CustomButton
                    to="/booking"
                    text="–ó–∞–±—Ä–æ–Ω–∏—Ä–æ–≤–∞—Ç—å"
                    variant="green"
                    className="w-full py-2 text-sm hover:shadow-md transition"
                  />
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="col-span-3 text-center text-gray-500">–ú–∞—Å—Å–∞–∂–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã</div>
        )}
      </div>
    </section>
  );
}

export default Massages;
