import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createBooking, resetBooking } from '../redux/slices/bookingSlice';
import { toast, ToastContainer } from 'react-toastify'; // Опционально: уведомления
import 'react-toastify/dist/ReactToastify.css';

function Booking() {
  const dispatch = useDispatch();

  // Состояние формы
  const [formData, setFormData] = useState({
    date: '',
    duration_hours: 1,
    bath_id: '',
    guests: 1,
    name: '',
    phone: '',
    email: '',
    notes: '',
  });

  // Состояние из Redux
  const { loading, success, error } = useSelector((state) => state.booking);

  // Обработчик изменения полей
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Обработчик отправки формы
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Валидация email (опционально)
    if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      toast.error('Некорректный email');
      return;
    }

    // Отправляем бронь
    dispatch(createBooking(formData))
      .unwrap()
      .then(() => {
        toast.success('✅ Бронь успешно создана! Мы свяжемся с вами.');
        setFormData({
          date: '',
          duration_hours: 1,
          bath_id: '',
          guests: 1,
          name: '',
          phone: '',
          email: '',
          notes: '',
        });
      })
      .catch((err) => {
        toast.error(`❌ Ошибка: ${err}`);
      });
  };

  // Сброс состояния после закрытия уведомления (опционально)
  const handleReset = () => {
    dispatch(resetBooking());
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-amber-50 via-white to-green-50 py-12 px-4 sm:px-6 lg:px-8 mt-14">
      <ToastContainer position="top-right" autoClose={5000} onClose={handleReset} />

      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-5">
          <h1 className="text-4xl sm:text-5xl font-light text-gray-800 mb-4">Забронировать баню</h1>
          <p className="text-lg text-gray-600 font-extralight max-w-2xl mx-auto">
            Выберите удобную дату и время — мы подготовим парную к вашему приходу.
            Уют, чистота и ароматные веники уже ждут вас!
          </p>
        </div>

        <div className="bg-white rounded-3xl shadow-xl p-8 md:p-10 border border-gray-100">
          <form onSubmit={handleSubmit} className="space-y-8">
            {/* Дата и часы */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-2">
                  Дата посещения
                </label>
                <input
                  type="date"
                  id="date"
                  name="date"
                  value={formData.date}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  required
                />
              </div>
              <div>
                <label htmlFor="duration_hours" className="block text-sm font-medium text-gray-700 mb-2">
                  Продолжительность (в часах)
                </label>
                <input
                  type="number"
                  id="duration_hours"
                  name="duration_hours"
                  min="1"
                  max="12"
                  value={formData.duration_hours}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  required
                />
              </div>
            </div>

            {/* Выбор бани */}
            <div>
              <label htmlFor="bath_id" className="block text-sm font-medium text-gray-700 mb-2">
                Выберите баню
              </label>
              <select
                id="bath_id"
                name="bath_id"
                value={formData.bath_id}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent bg-white"
                required
              >
                <option value="">Выберите баню</option>
                <option value="1">Кедровая баня</option>
                <option value="2">Нефритовая баня</option>
                <option value="3">Липовая баня</option>
              </select>
            </div>

            {/* Гости */}
            <div>
              <label htmlFor="guests" className="block text-sm font-medium text-gray-700 mb-2">
                Количество гостей
              </label>
              <input
                type="number"
                id="guests"
                name="guests"
                min="1"
                max="12"
                value={formData.guests}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                required
              />
            </div>

            {/* Имя и телефон */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
                  Ваше имя
                </label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="Иван"
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  required
                />
              </div>
              <div>
                <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-2">
                  Телефон
                </label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  placeholder="+7 (999) 123-45-67"
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  required
                />
              </div>
            </div>

            {/* Email (добавим, если нужно) */}
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">
                Email (опционально)
              </label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="ivan@example.com"
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
              />
            </div>

            {/* Дополнительно */}
            <div>
              <label htmlFor="notes" className="block text-sm font-medium text-gray-700 mb-2">
                Дополнительно (веники, шашлык, аренда посуды и т.д.)
              </label>
              <textarea
                id="notes"
                name="notes"
                rows="3"
                value={formData.notes}
                onChange={handleChange}
                placeholder="Хотим кедровые веники и мангал на 5 часов..."
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent resize-none"
              ></textarea>
            </div>

            {/* Кнопка */}
            <div className="pt-4 text-center">
              <button
                type="submit"
                disabled={loading}
                className={`px-10 py-3 text-white rounded-xl text-lg font-medium transition-all duration-300 ${
                  loading
                    ? 'bg-gray-500 cursor-not-allowed'
                    : 'bg-green-600 hover:bg-green-700 hover:shadow-xl'
                }`}
              >
                {loading ? 'Отправка...' : 'Забронировать'}
              </button>
            </div>

            {/* Успешное сообщение (можно убрать, если используешь toast) */}
            {success && (
              <div className="text-center text-green-600 font-medium">
                ✅ Бронь успешно создана! Мы свяжемся с вами.
              </div>
            )}
            {error && !success && (
              <div className="text-center text-red-600 font-medium">
                ❌ Ошибка: {error}
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
}

export default Booking;
