import { useState } from 'react';
import {
    useGetBathsQuery,
    useCreateBathMutation,
    useUpdateBathMutation,
    useDeleteBathMutation,
} from '../../../redux/slices/apiSlice';

function AdminBaths() {
    // ะะฐะณััะถะฐะตะผ ัะฟะธัะพะบ ะฑะฐะฝั
    const { baths, isLoading, error } = useGetBathsQuery();

    // ะััะฐัะธะธ
    const [createBath] = useCreateBathMutation();
    const [updateBath] = useUpdateBathMutation();
    const [deleteBath] = useDeleteBathMutation();

    // ะกะพััะพัะฝะธั ัะพัะผ
    const [isAddingNew, setIsAddingNew] = useState(false);
    const [newBath, setNewBath] = useState({
        name: '',
        title: '',
        cost: '',
        description: '',
        photo_urls: [],
        features: [],
    });

    const [editingBath, setEditingBath] = useState(null); // ๐ ะะฑัะตะบั ะฑะฐะฝะธ ะดะปั ัะตะดะฐะบัะธัะพะฒะฐะฝะธั
    const [deletingId, setDeletingId] = useState(null);

    // ะคะพัะผะฐ ะดะปั ะทะฐะณััะทะบะธ ัะพัะพ (File input)
    const [selectedFiles, setSelectedFiles] = useState([]);

    // ะัะบัััั ัะพัะผั ะดะพะฑะฐะฒะปะตะฝะธั
    const handleAddClick = () => {
        setIsAddingNew(true);
        setNewBath({
            name: '',
            title: '',
            cost: '',
            description: '',
