import { createPortal } from 'react-dom';
import { useEffect, useState } from 'react';
import {
  useGetBathsQuery,
  useCreateReservationMutation,
  useGetBroomsQuery,
  useGetMassagesQuery,
  useGetMenuCategoriesQuery,
  useGetAllMenuItemsQuery,
} from '../../../redux/slices/apiSlice';

function AddBookingModal({ isOpen, onClose, onAdd, onEdit, booking }) {

  const isEditing = !!booking;

  // Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð¸Ñ€ÑƒÐµÐ¼ Ñ„Ð¾Ñ€Ð¼Ñƒ
  const [formData, setFormData] = useState({
    bath_id: '',
    start_datetime: '',
    end_datetime: '',
    client_name: '',
    client_phone: '',
    client_email: '',
    notes: '',
    brooms: [],
    menu_items: [],
    massages: [],
  });

  // Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð´Ð°Ð½Ð½Ñ‹Ñ…
  const { data: baths = [], isLoading: isLoadingBaths } = useGetBathsQuery();
  const { data: brooms = [], isLoading: isLoadingBrooms } = useGetBroomsQuery();
  const { data: massages = [], isLoading: isLoadingMassages } = useGetMassagesQuery();
  const { data: categories = [], isLoading: isLoadingCategories } = useGetMenuCategoriesQuery();
  const { data: allMenuItems = [], isLoading: isLoadingMenuItems } = useGetAllMenuItemsQuery();

  const [createReservation, { isLoading: isCreating }] = useCreateReservationMutation();

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð´Ð»Ñ UI
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [kitchenItems, setKitchenItems] = useState([]);
  const [showBrooms, setShowBrooms] = useState(false);
  const [showMassages, setShowMassages] = useState(false);
  const [showKitchen, setShowKitchen] = useState(false);

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.bath_id || !formData.start_datetime || !formData.end_datetime || !formData.client_name) {
      alert('Ð—Ð°Ð¿Ð¾Ð»Ð½Ð¸Ñ‚Ðµ Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ðµ Ð¿Ð¾Ð»Ñ: Ð±Ð°Ð½Ñ, Ð²Ñ€ÐµÐ¼Ñ, Ð¸Ð¼Ñ ÐºÐ»Ð¸ÐµÐ½Ñ‚Ð°');
      return;
    }

    if (new Date(formData.start_datetime) >= new Date(formData.end_datetime)) {
      alert('Ð’Ñ€ÐµÐ¼Ñ Ð¾ÐºÐ¾Ð½Ñ‡Ð°Ð½Ð¸Ñ Ð´Ð¾Ð»Ð¶Ð½Ð¾ Ð±Ñ‹Ñ‚ÑŒ Ð¿Ð¾Ð·Ð¶Ðµ Ð½Ð°Ñ‡Ð°Ð»Ð°');
      return;
    }

    try {
      if (isEditing && onEdit) {
        await onEdit({
          ...formData,
          reservation_id: booking.reservation_id,
        });
        alert('âœ… Ð‘Ñ€Ð¾Ð½ÑŒ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð°!');
      } else if (onAdd) {
        await createReservation(formData).unwrap();
        alert('âœ… Ð‘Ñ€Ð¾Ð½ÑŒ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ ÑÐ¾Ð·Ð´Ð°Ð½Ð°!');
      }

      // Ð¡Ð±Ñ€Ð°ÑÑ‹Ð²Ð°ÐµÐ¼ Ñ„Ð¾Ñ€Ð¼Ñƒ
      setFormData({
        bath_id: '',
        start_datetime: '',
        end_datetime: '',
        client_name: '',
        client_phone: '',
        client_email: '',
        notes: '',
        brooms: [],
        menu_items: [],
        massages: [],
      });

      onClose();
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ°:', error);
      let message = isEditing ? 'ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð¾Ð±Ð½Ð¾Ð²Ð¸Ñ‚ÑŒ Ð±Ñ€Ð¾Ð½ÑŒ' : 'ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ ÑÐ¾Ð·Ð´Ð°Ñ‚ÑŒ Ð±Ñ€Ð¾Ð½ÑŒ';
      if (error.data?.detail) {
        message = error.data.detail;
      }
      alert('âŒ ' + message);
    }
  };

  // Ð£Ð¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ Ð²ÐµÐ½Ð¸ÐºÐ°Ð¼Ð¸
  const handleBroomChange = (broomId, quantity) => {
    setFormData(prev => {
      const existing = prev.brooms.find(b => b.broom_id === broomId);
      let updated = [...prev.brooms];

      if (quantity <= 0) {
        updated = updated.filter(b => b.broom_id !== broomId);
      } else {
        if (existing) {
          updated = updated.map(b =>
            b.broom_id === broomId ? { ...b, quantity } : b
          );
        } else {
          updated.push({ broom_id: broomId, quantity });
        }
      }

      return { ...prev, brooms: updated };
    });
  };

  // Ð£Ð¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ Ð¼Ð°ÑÑÐ°Ð¶Ð°Ð¼Ð¸
  const handleMassageChange = (massageId, quantity) => {
    setFormData(prev => {
      const currentList = prev.massages || [];
      const existing = currentList.find(m => m.massage_id === massageId);
      let updated = [...currentList];

      if (quantity <= 0) {
        updated = updated.filter(m => m.massage_id !== massageId);
      } else {
        if (existing) {
          updated = updated.map(m =>
            m.massage_id === massageId ? { ...m, quantity } : m
          );
        } else {
          updated.push({ massage_id: massageId, quantity });
        }
      }

      return { ...prev, massages: updated };
    });
  };

  // Ð£Ð¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ Ð±Ð»ÑŽÐ´Ð°Ð¼Ð¸
  const handleMenuItemChange = (itemId, quantity) => {
    setFormData(prev => {
      const existing = prev.menu_items.find(m => m.menu_item_id === itemId);
      let updated = [...prev.menu_items];

      if (quantity <= 0) {
        updated = updated.filter(m => m.menu_item_id !== itemId);
      } else {
        if (existing) {
          updated = updated.map(m =>
            m.menu_item_id === itemId ? { ...m, quantity } : m
          );
        } else {
          updated.push({ menu_item_id: itemId, quantity });
        }
      }

      return { ...prev, menu_items: updated };
    });
  };

  // ÐŸÑ€Ð¸ Ð²Ñ‹Ð±Ð¾Ñ€Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸ â€” Ñ„Ð¸Ð»ÑŒÑ‚Ñ€ÑƒÐµÐ¼ Ñ‚Ð¾Ð²Ð°Ñ€Ñ‹
  useEffect(() => {
    if (selectedCategories.length > 0 && allMenuItems.length > 0) {
      const filtered = allMenuItems.filter(item =>
        selectedCategories.includes(item.category)
      );
      setKitchenItems(filtered);
    } else {
      setKitchenItems([]);
    }
  }, [selectedCategories, allMenuItems]);

  // ðŸ§© Ð—Ð°Ð¿Ð¾Ð»Ð½ÑÐµÐ¼ Ñ„Ð¾Ñ€Ð¼Ñƒ, ÐµÑÐ»Ð¸ Ð¿ÐµÑ€ÐµÐ´Ð°Ð½Ð° Ð±Ñ€Ð¾Ð½ÑŒ (Ñ€ÐµÐ¶Ð¸Ð¼ Ñ€ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ)
  useEffect(() => {
    if (booking && brooms.length > 0 && massages.length > 0 && allMenuItems.length > 0) {
      setFormData({
        bath_id: booking.bath?.bath_id || booking.bath_id || '',
        start_datetime: booking.start_datetime ? new Date(booking.start_datetime).toISOString().slice(0, 16) : '',
        end_datetime: booking.end_datetime ? new Date(booking.end_datetime).toISOString().slice(0, 16) : '',
        client_name: booking.client_name || '',
        client_phone: booking.client_phone || '',
