// src/store/store.js
import { configureStore } from '@reduxjs/toolkit';
import { massagesApiSlice } from './slices/massagesApiSlice'; // ðŸ‘ˆ Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚Ð¸Ñ€ÑƒÐµÐ¼ Ð½Ð¾Ð²Ñ‹Ð¹ slice
import bookingReducer from './slices/bookingSlice';

export const store = configureStore({
  reducer: {
    [massagesApiSlice.reducerPath]: massagesApiSlice.reducer,
    booking: bookingReducer,
  },
  middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware().concat(massagesApiSlice.middleware),
});
