import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

export const massagesApiSlice = createApi({
  baseQuery: fetchBaseQuery({
    baseUrl: 'http://127.0.0.1:8000/api', // üëà –ü—Ä–∞–≤–∏–ª—å–Ω—ã–π –±–∞–∑–æ–≤—ã–π URL
    prepareHeaders: (headers) => {

      return headers;
    },
  }),
  endpoints: (builder) => ({
    getMassages: builder.query({
      query: () => '/massages',
      transformResponse: (response) => {

        return response;
      },
    }),
    getMassageById: builder.query({
      query: (massageId) => `/massages/${massageId}`,
    }),
  }),
});

// –≠–∫—Å–ø–æ—Ä—Ç–∏—Ä—É–µ–º —Ö—É–∫–∏
export const { useGetMassagesQuery, useGetMassageByIdQuery } = massagesApiSlice;
