import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  massages: [],
  status: 'idle', // 'idle' | 'loading' | 'succeeded' | 'failed'
  error: null,
};

const massagesSlice = createSlice({
  name: 'massages',
  initialState,
  reducers: {
    // Редюсер для установки массажей
    setMassages: (state, action) => {
      state.massages = action.payload;
      state.status = 'succeeded';
    },
    // Редюсер для загрузки
    fetchMassagesRequest: (state) => {
      state.status = 'loading';
    },
    // Редюсер для ошибки
    fetchMassagesFailed: (state, action) => {
      state.status = 'failed';
      state.error = action.payload;
    },
  },
});

export const { setMassages, fetchMassagesRequest, fetchMassagesFailed } = massagesSlice.actions;
export default massagesSlice.reducer;
