import { useState, useMemo, useEffect } from 'react';
import ReservationsFilters from '../Reservations/ReservationsFilters';
import BookingDetailsModal from '../Reservations/BookingDetailsModal';
import AddBookingModal from '../Reservations/AddBookingModal';
import { useGetBathsQuery, useGetReservationsByDateQuery } from '../../../redux/slices/apiSlice';

function AdminReservations() {
  const today = new Date().toISOString().split('T')[0];
  const [filters, setFilters] = useState({ date: today });
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);

  // –ü–æ–ª—É—á–∞–µ–º –±–∞–Ω–∏ ‚Äî —Å –ø—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ–π –ø–µ—Ä–µ–∑–∞–≥—Ä—É–∑–∫–æ–π
  const {
    baths = [],
    isLoading: isLoadingBaths,
    error: bathsError,
    isFetching: isFetchingBaths,
    refetch: refetchBaths
  } = useGetBathsQuery(undefined, {
    refetchOnMountOrArgChange: true,
  });
  const emergencyBaths = [
    { bath_id: "emergency_1", bathName: "–ê–≤–∞—Ä–∏–π–Ω–∞—è –±–∞–Ω—è 1", bookings: [], hasBookings: false },
    { bath_id: "emergency_2", bathName: "–ê–≤–∞—Ä–∏–π–Ω–∞—è –±–∞–Ω—è 2", bookings: [], hasBookings: false },
  ];

  console.log('üö® [EMERGENCY] –ê–≤–∞—Ä–∏–π–Ω—ã–π —Å–ø–∏—Å–æ–∫ –±–∞–Ω—å:', emergencyBaths);
  // üö® –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ –ø–µ—Ä–µ–∑–∞–≥—Ä—É–∂–∞–µ–º –∏ –ª–æ–≥–∏—Ä—É–µ–º
  useEffect(() => {
    console.log('üîß [AdminReservations] –ó–∞–ø—É—Å–∫–∞–µ–º refetch –±–∞–Ω—å...');
    refetchBaths().then(result => {
      console.log('‚úÖ [AdminReservations] –†–µ–∑—É–ª—å—Ç–∞—Ç refetch:', result);
      if (result.data) {
        console.log('üéâ [AdminReservations] –ü–æ–ª—É—á–µ–Ω—ã –±–∞–Ω–∏:', result.data);
      }
      if (result.error) {
        console.error('‚ùå [AdminReservations] –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –±–∞–Ω—å:', result.error);
      }
    });
  }, [refetchBaths]);

  console.log('üìä [Baths] isLoading:', isLoadingBaths);
  console.log('üìä [Baths] isFetching:', isFetchingBaths);
  console.log('‚ùå [Baths] error:', bathsError);
  console.log('üîç [AdminReservations] baths:', baths);
  // –ü–æ–ª—É—á–∞–µ–º –±—Ä–æ–Ω–∏
  const {
    data: reservations = [],
    isLoading: isLoadingReservations,
    error,
    isError
  } = useGetReservationsByDateQuery(
    { date: filters.date || today },
    { refetchOnMountOrArgChange: false }
  );

  // üîç –õ–û–ì–ò: –ø—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –ø—Ä–∏—à–ª–æ
  console.log('üîç [AdminReservations] baths:', baths);
  console.log('üîç [AdminReservations] reservations:', reservations);
  console.log('üîç [AdminReservations] filters.date:', filters.date);

  if (bathsError) {
    console.error('‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –±–∞–Ω—å:', bathsError);
  }

  // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –≤—Ä–µ–º–µ–Ω–Ω—ã—Ö —Å–ª–æ—Ç–æ–≤ 9:00 - 23:30
  const timeSlots = useMemo(() => {
    if (!filters.date) return [];
    const slots = [];
    for (let hour = 9; hour < 24; hour++) {
      for (let minute of [0, 30]) {
        const timeStr = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
        const dateTime = new Date(`${filters.date}T${timeStr}:00`);
        slots.push({
          time: timeStr,
          start: dateTime,
          end: new Date(dateTime.getTime() + 30 * 60000),
        });
      }
    }
    return slots;
  }, [filters.date]);

  // –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –¥–∞–Ω–Ω—ã—Ö: –¥–ª—è –∫–∞–∂–¥–æ–π –±–∞–Ω–∏ ‚Äî –µ—ë –±—Ä–æ–Ω–∏ –Ω–∞ –≤—ã–±—Ä–∞–Ω–Ω—É—é –¥–∞—Ç—É
  // –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –¥–∞–Ω–Ω—ã—Ö: –¥–ª—è –∫–∞–∂–¥–æ–π –±–∞–Ω–∏ ‚Äî –µ—ë –±—Ä–æ–Ω–∏ –Ω–∞ –≤—ã–±—Ä–∞–Ω–Ω—É—é –¥–∞—Ç—É
  const bathsToDisplay = useMemo(() => {
    console.log('üîç [DEBUG] –ü–æ–ª—É—á–µ–Ω–Ω—ã–µ –±–∞–Ω–∏:', baths);
    console.log('üîç [DEBUG] –ü–æ–ª—É—á–µ–Ω–Ω—ã–µ –±—Ä–æ–Ω–∏:', reservations);
    console.log('üîç [DEBUG] –í—ã–±—Ä–∞–Ω–Ω–∞—è –¥–∞—Ç–∞:', filters.date);

    if (!Array.isArray(baths) || baths.length === 0) {
      console.warn('‚ö†Ô∏è –°–ø–∏—Å–æ–∫ –±–∞–Ω—å –ø—É—Å—Ç –∏–ª–∏ –Ω–µ —è–≤–ª—è–µ—Ç—Å—è –º–∞—Å—Å–∏–≤–æ–º');
      return [];
    }

    // –£–±–µ–¥–∏–º—Å—è, —á—Ç–æ reservations ‚Äî –º–∞—Å—Å–∏–≤
    const safeReservations = Array.isArray(reservations) ? reservations : [];

    // –°–æ–∑–¥–∞—ë–º –º–∞–ø—É –±—Ä–æ–Ω–µ–π –ø–æ bath_id –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –¥–æ—Å—Ç—É–ø–∞
    const reservationsByBathId = {};
    safeReservations.forEach(res => {
      if (!res?.start_datetime || !res?.bath_id) return;

      const d = new Date(res.start_datetime);
      if (isNaN(d.getTime())) return;

      const resDate = `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}-${String(d.getDate()).padStart(2, '0')}`;
      if (resDate !== filters.date) return;

      const bathIdKey = String(res.bath_id); // –ü—Ä–∏–≤–æ–¥–∏–º –∫ —Å—Ç—Ä–æ–∫–µ –¥–ª—è –Ω–∞–¥—ë–∂–Ω–æ—Å—Ç–∏

      if (!reservationsByBathId[bathIdKey]) {
        reservationsByBathId[bathIdKey] = [];
      }
      reservationsByBathId[bathIdKey].push(res);
    });

    console.log('üìä [DEBUG] –ú–∞–ø–∞ –±—Ä–æ–Ω–µ–π –ø–æ bath_id:', reservationsByBathId);

    // –§–æ—Ä–º–∏—Ä—É–µ–º –∏—Ç–æ–≥–æ–≤—ã–π –º–∞—Å—Å–∏–≤ ‚Äî –í–°–ï –±–∞–Ω–∏, –¥–∞–∂–µ –µ—Å–ª–∏ —É –Ω–∏—Ö –Ω–µ—Ç –±—Ä–æ–Ω–µ–π
    const result = baths.map(bath => {
      // –û–ø—Ä–µ–¥–µ–ª—è–µ–º ID –±–∞–Ω–∏ ‚Äî —Å –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–æ–º: bath_id ‚Üí id ‚Üí –≤—Ä–µ–º–µ–Ω–Ω—ã–π ID
      let bathId = null;
      if (bath.bath_id !== undefined && bath.bath_id !== null) {
        bathId = String(bath.bath_id); // –ü—Ä–∏–≤–æ–¥–∏–º –∫ —Å—Ç—Ä–æ–∫–µ
      } else if (bath.id !== undefined && bath.id !== null) {
        bathId = String(bath.id); // –ü—Ä–∏–≤–æ–¥–∏–º –∫ —Å—Ç—Ä–æ–∫–µ
      } else {
        bathId = `fallback_${Math.random().toString(36).substr(2, 9)}`;
        console.warn('‚ö†Ô∏è –£ –±–∞–Ω–∏ –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç –∏ bath_id, –∏ id:', bath);
      }

      const bathName = bath.name || bath.title || '–ë–µ–∑ –Ω–∞–∑–≤–∞–Ω–∏—è';
      const bookings = reservationsByBathId[bathId] || []; // –ë—Ä–æ–Ω–∏ –¥–ª—è —ç—Ç–æ–π –±–∞–Ω–∏ (–º–æ–∂–µ—Ç –±—ã—Ç—å [])

      console.log(`üìã –ë–∞–Ω—è ID: ${bathId}, –ù–∞–∑–≤–∞–Ω–∏–µ: ${bathName}, –ë—Ä–æ–Ω–µ–π: ${bookings.length}`);

      return {
        bath_id: bathId,
        bathName: bathName,
        bookings: bookings,
        hasBookings: bookings.length > 0,
      };
    });

    console.log('‚úÖ [bathsToDisplay] –ò—Ç–æ–≥–æ–≤—ã–π –º–∞—Å—Å–∏–≤ (–¥–ª–∏–Ω–∞: ' + result.length + '):', result);

    // –ï—Å–ª–∏ –º–∞—Å—Å–∏–≤ –ø—É—Å—Ç ‚Äî –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –∑–∞–≥–ª—É—à–∫—É –¥–ª—è –æ—Ç–ª–∞–¥–∫–∏
    if (result.length === 0 && baths.length > 0) {
      console.error('‚ùå [CRITICAL] bathsToDisplay –ø—É—Å—Ç, —Ö–æ—Ç—è baths –Ω–µ –ø—É—Å—Ç! –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∑–∞–≥–ª—É—à–∫–∏.');
      return baths.map((bath, index) => ({
        bath_id: `debug_${index}`,
        bathName: bath.name || `–ë–∞–Ω—è ${index + 1}`,
        bookings: [],
        hasBookings: false,
      }));
    }

    return result;
  }, [filters.date, baths, reservations]);

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏
  const handleApply = (newFilters) => {
    console.log('üìÖ –ü—Ä–∏–º–µ–Ω—ë–Ω —Ñ–∏–ª—å—Ç—Ä:', newFilters);
    setFilters(newFilters);
  };

  const handleViewBooking = (booking) => {
    console.log('üëÅÔ∏è –ü—Ä–æ—Å–º–æ—Ç—Ä –±—Ä–æ–Ω–∏:', booking);
    setSelectedBooking(booking);
  };

  const handleEditBooking = (booking) => {
    console.log('‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –±—Ä–æ–Ω—å:', booking);
    setSelectedBooking(null);
  };

  const handleDeleteBooking = (id) => {
    if (window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç—É –±—Ä–æ–Ω—å?')) {
      console.log('üóëÔ∏è –£–¥–∞–ª–µ–Ω–∏–µ –±—Ä–æ–Ω–∏ ID:', id);
      setSelectedBooking(null);
    }
  };

  const handleCloseModal = () => {
    setSelectedBooking(null);
  };

  const handleAddBooking = (newBooking) => {
    console.log('‚ûï –î–æ–±–∞–≤–ª–µ–Ω–∞ –Ω–æ–≤–∞—è –±—Ä–æ–Ω—å:', newBooking);
    newBooking.reservation_id = newBooking.reservation_id || Date.now();
    setIsAddModalOpen(false);
    setFilters(prev => ({ ...prev })); // –¢—Ä–∏–≥–≥–µ—Ä –ø–µ—Ä–µ—Ä–µ–Ω–¥–µ—Ä–∞
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });
  };

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –æ—à–∏–±–æ–∫ API
  if (isError && error) {
    console.error('‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–π:', error);
    return (
      <div className="p-6 bg-red-50 border border-red-200 rounded-xl">
        <h3 className="text-lg font-medium text-red-800">–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏</h3>
        <p className="text-red-700 mt-2">
          {error.status === 401
            ? '–ù–µ –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–æ–π–¥–∏—Ç–µ —Å–Ω–æ–≤–∞.'
            : error.message || '–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞'}
        </p>
        {error.status === 401 && (
          <button
            onClick={() => {
              localStorage.removeItem('access_token');
              window.location.href = '/admin/login';
            }}
            className="mt-4 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
          >
            –ü–µ—Ä–µ–π—Ç–∏ –∫ –ª–æ–≥–∏–Ω—É
          </button>
        )}
      </div>
    );
  }

  // –ò–Ω–¥–∏–∫–∞—Ü–∏—è –∑–∞–≥—Ä—É–∑–∫–∏
  if (isLoadingBaths || isLoadingReservations) {
    console.log('‚è≥ –ò–¥—ë—Ç –∑–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö...');
    return (
      <div className="flex items-center justify-center py-16">
        <div className="text-gray-500">–ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö...</div>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="text-2xl font-semibold text-gray-800 mb-6">
        –ó–∞–ø–∏—Å–∏ –Ω–∞ {new Date(filters.date).toLocaleDateString('ru-RU', {
          day: 'numeric',
          month: 'long',
          year: 'numeric',
        })}
      </h2>

      <ReservationsFilters
        onApply={handleApply}
        onAddBooking={() => setIsAddModalOpen(true)}
      />

      <div className="overflow-x-auto pb-4 -mx-4 px-4">
        <div className="inline-flex space-x-6 min-h-[600px]">
          {/* –ö–æ–ª–æ–Ω–∫–∞ –≤—Ä–µ–º–µ–Ω–∏ */}
          <div className="flex-shrink-0 w-32 bg-gray-50 p-4 border-r border-gray-200 sticky top-0">
            <div className="text-sm font-medium text-gray-700 mb-2">–í–†–ï–ú–Ø</div>
            {timeSlots.map((slot, idx) => (
              <div key={idx} className="text-sm text-gray-600 py-2 h-14 flex items-center">
                {formatTime(slot.start)}
              </div>
            ))}
          </div>

          {/* –ö–æ–ª–æ–Ω–∫–∏ —Å –±–∞–Ω—è–º–∏ */}
          {bathsToDisplay.length === 0 ? (
            <div className="text-gray-500 p-8">
              {isLoadingBaths ? '–ó–∞–≥—Ä—É–∑–∫–∞ –±–∞–Ω—å...' : '–ù–µ—Ç –¥–æ—Å—Ç—É–ø–Ω—ã—Ö –±–∞–Ω—å –∏–ª–∏ –Ω–µ —É–¥–∞–ª–æ—Å—å —Å–æ–ø–æ—Å—Ç–∞–≤–∏—Ç—å –±—Ä–æ–Ω–∏ —Å –¥–∞—Ç–æ–π'}
            </div>
          ) : (
            bathsToDisplay.map((bath) => (
              <div key={bath.bath_id} className="flex-shrink-0 w-96">
                <div className="bg-white rounded-xl shadow-md border border-gray-200 overflow-hidden">
                  <div className="p-4 border-b border-gray-200 bg-gray-50">
                    <h3 className="text-lg font-semibold text-gray-800">{bath.bathName}</h3>
                  </div>

                  {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏–æ–Ω–Ω–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ, –µ—Å–ª–∏ –±—Ä–æ–Ω–µ–π –Ω–µ—Ç */}
                  {!bath.hasBookings && (
                    <div className="p-3 bg-yellow-50 border-b border-yellow-200 text-center text-yellow-800 text-sm font-medium">
                      –ù–∞ —ç—Ç—É –¥–∞—Ç—É –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–π –Ω–µ—Ç
                    </div>
                  )}

                  {/* –¢–∞–±–ª–∏—Ü–∞ –≤—Ä–µ–º–µ–Ω–Ω—ã—Ö —Å–ª–æ—Ç–æ–≤ ‚Äî –≤—Å–µ–≥–¥–∞ –æ—Ç–æ–±—Ä–∞–∂–∞–µ—Ç—Å—è */}
                  <div className="overflow-x-auto">
                    <table className="w-full table-fixed border-separate border-spacing-0">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                            –°—Ç–∞—Ç—É—Å
                          </th>
                        </tr>
                      </thead>
                      <tbody>
                        {timeSlots.map((slot, idx) => {
                          const normalizedBookings = bath.bookings.map(booking => ({
                            ...booking,
                            start: new Date(booking.start_datetime),
                            end: new Date(booking.end_datetime),
                          }));

                          const overlapping = normalizedBookings.find((res) =>
                            res.start < slot.end && res.end > slot.start
                          );

                          return (
                            <tr key={idx} className="border-b border-gray-100 hover:bg-gray-25 transition-colors">
                              <td className="px-6 py-0 relative bg-gray-50" style={{ height: '56px', overflow: 'visible' }}>
                                {overlapping ? (
                                  <div
                                    className="absolute inset-x-0 bg-red-50 border-4 border-blue-500 text-red-800 text-sm font-medium rounded-lg px-3 flex items-center justify-center cursor-pointer hover:shadow transition-all duration-200 group"
                                    style={{
                                      top: 0,
                                      height: `${((overlapping.end - overlapping.start) / (30 * 60000)) * 100}%`,
                                      zIndex: 10,
                                      overflow: 'visible',
                                    }}
                                    title={`${overlapping.client_name} ‚Ä¢ ${formatTime(overlapping.start)} ‚Äì ${formatTime(overlapping.end)}`}
                                    onClick={() => handleViewBooking(overlapping)}
                                  >
                                    <div className="w-1 bg-red-400 rounded-full absolute left-0 top-1 bottom-1 group-hover:w-2 transition-all duration-200"></div>
                                    <span className="truncate max-w-full text-center leading-tight font-medium">
                                      {overlapping.client_name}
                                    </span>
                                  </div>
                                ) : (
                                  <div className="absolute inset-0 flex items-center justify-center text-green-600 text-sm font-medium bg-green-50 rounded-lg border border-green-200">
