// src/pages/Admin/AdminKitchen.jsx

import { useState, useEffect } from 'react';
import {
  useGetMenuCategoriesQuery,
  useCreateMenuCategoryMutation,
  useUpdateMenuCategoryMutation,
  useDeleteMenuCategoryMutation,
  useGetAllMenuItemsQuery,
  useCreateMenuItemMutation,
  useUpdateMenuItemMutation,
  useDeleteMenuItemMutation,
} from '../../../redux/slices/apiSlice';

function AdminKitchen() {
  // ================
  // ðŸ“‚ ÐšÐÐ¢Ð•Ð“ÐžÐ Ð˜Ð˜
  // ================
  const { data: categories = [], isLoading: loadingCategories } = useGetMenuCategoriesQuery();
  const [createCategory] = useCreateMenuCategoryMutation();
  const [updateCategory] = useUpdateMenuCategoryMutation();
  const [deleteCategory] = useDeleteMenuCategoryMutation();

  const [editingCategoryId, setEditingCategoryId] = useState(null);
  const [editCategoryForm, setEditCategoryForm] = useState({ slug: '', name: '', order: 0 });
  const [isAddingNewCategory, setIsAddingNewCategory] = useState(false);
  const [newCategory, setNewCategory] = useState({ slug: '', name: '', order: 0 });
  const [deletingCategoryId, setDeletingCategoryId] = useState(null);

  // ================
  // ðŸ½ï¸ Ð¢ÐžÐ’ÐÐ Ð«
  // ================
  const { data: items = [], isLoading: loadingItems } = useGetAllMenuItemsQuery();
  const [createItem] = useCreateMenuItemMutation();
  const [updateItem] = useUpdateMenuItemMutation();
  const [deleteItem] = useDeleteMenuItemMutation();

  const [editingItemId, setEditingItemId] = useState(null);
  const [editItemForm, setEditItemForm] = useState({ name: '', description: '', price: '', category_id: '' });
  const [isAddingNewItem, setIsAddingNewItem] = useState(false);
  const [newItem, setNewItem] = useState({ name: '', description: '', price: '', category_id: '' });
  const [deletingItemId, setDeletingItemId] = useState(null);

  // ================
  // ðŸ”„ EFFECTS
  // ================

  useEffect(() => {
    if (editingCategoryId && categories.length > 0) {
      const cat = categories.find(c => c.id === editingCategoryId);
      if (cat) {
        setEditCategoryForm({
          slug: cat.slug,
          name: cat.name,
          order: cat.order,
        });
      }
    }
  }, [editingCategoryId, categories]);

  useEffect(() => {
    if (editingItemId && items.length > 0) {
      const item = items.find(i => i.id === editingItemId);
      if (item) {
        setEditItemForm({
          name: item.name,
          description: item.description,
          price: item.price,
          category_id: item.category.id,
        });
      }
    }
  }, [editingItemId, items]);

  // ================
  // ðŸ“‚ ÐšÐÐ¢Ð•Ð“ÐžÐ Ð˜Ð˜ â€” HANDLERS
  // ================

  const handleEditCategoryClick = (category) => {
    setEditingCategoryId(category.id);
  };

  const handleSaveCategoryEdit = async () => {
    if (!editCategoryForm.slug || !editCategoryForm.name) return;

    try {
      await updateCategory({
        id: editingCategoryId,
        ...editCategoryForm,
        order: Number(editCategoryForm.order),
      }).unwrap();
      setEditingCategoryId(null);
      setEditCategoryForm({ slug: '', name: '', order: 0 });
    } catch (err) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸:', err);
    }
  };

  const handleCancelCategoryEdit = () => {
    setEditingCategoryId(null);
    setEditCategoryForm({ slug: '', name: '', order: 0 });
  };

  const handleAddCategoryClick = () => {
    setIsAddingNewCategory(true);
    setNewCategory({ slug: '', name: '', order: 0 });
  };

  const handleAddCategory = async () => {
    if (!newCategory.slug || !newCategory.name) return;

    try {
      await createCategory({
        ...newCategory,
        order: Number(newCategory.order),
      }).unwrap();
      setIsAddingNewCategory(false);
      setNewCategory({ slug: '', name: '', order: 0 });
    } catch (err) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ñ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸:', err);
    }
  };

  const handleCancelCategoryAdd = () => {
    setIsAddingNewCategory(false);
    setNewCategory({ slug: '', name: '', order: 0 });
  };

  const handleDeleteCategoryClick = (id) => {
    setDeletingCategoryId(id);
  };

  const handleConfirmCategoryDelete = async () => {
    try {
      await deleteCategory(deletingCategoryId).unwrap();
      setDeletingCategoryId(null);
    } catch (err) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ñ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸:', err);
    }
  };

  const handleCancelCategoryDelete = () => {
    setDeletingCategoryId(null);
  };

  // ================
  // ðŸ½ï¸ Ð¢ÐžÐ’ÐÐ Ð« â€” HANDLERS
  // ================

  const handleEditItemClick = (item) => {
    setEditingItemId(item.id);
  };

  const handleSaveItemEdit = async () => {
    if (!editItemForm.name || !editItemForm.price || !editItemForm.category_id) return;

    try {
      await updateItem({
        id: editingItemId,
        ...editItemForm,
        price: Number(editItemForm.price),
        category_id: Number(editItemForm.category_id),
      }).unwrap();
      setEditingItemId(null);
      setEditItemForm({ name: '', description: '', price: '', category_id: '' });
    } catch (err) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ Ñ‚Ð¾Ð²Ð°Ñ€Ð°:', err);
    }
  };

  const handleCancelItemEdit = () => {
    setEditingItemId(null);
    setEditItemForm({ name: '', description: '', price: '', category_id: '' });
  };

  const handleAddItemClick = () => {
    setIsAddingNewItem(true);
    setNewItem({
      name: '',
      description: '',
      price: '',
      category_id: categories.length > 0 ? categories[0].id : '',
    });
  };

  const handleAddItem = async () => {
    if (!newItem.name || !newItem.price || !newItem.category_id) return;

    try {
      await createItem({
        ...newItem,
        price: Number(newItem.price),
        category_id: Number(newItem.category_id),
      }).unwrap();
      setIsAddingNewItem(false);
      setNewItem({ name: '', description: '', price: '', category_id: '' });
    } catch (err) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ñ Ñ‚Ð¾Ð²Ð°Ñ€Ð°:', err);
    }
  };

  const handleCancelItemAdd = () => {
    setIsAddingNewItem(false);
    setNewItem({ name: '', description: '', price: '', category_id: '' });
  };

  const handleDeleteItemClick = (id) => {
    setDeletingItemId(id);
  };

  const handleConfirmItemDelete = async () => {
    try {
      await deleteItem(deletingItemId).unwrap();
      setDeletingItemId(null);
    } catch (err) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ñ Ñ‚Ð¾Ð²Ð°Ñ€Ð°:', err);
    }
  };

  const handleCancelItemDelete = () => {
    setDeletingItemId(null);
  };

  // ================
  // ðŸ§­ Ð—ÐÐ“Ð Ð£Ð—ÐšÐ
  // ================

  if (loadingCategories || loadingItems) {
    return (
      <div className="min-h-screen bg-gray-50 p-8 flex items-center justify-center">
        <div className="text-gray-600">Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð´Ð°Ð½Ð½Ñ‹Ñ… ÐºÑƒÑ…Ð½Ð¸...</div>
      </div>
    );
  }

  // ================
  // ðŸ–¥ï¸ Ð Ð•ÐÐ”Ð•Ð 
  // ================

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-7xl mx-auto">

        {/* Ð—Ð°Ð³Ð¾Ð»Ð¾Ð²Ð¾Ðº */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-800">ÐšÑƒÑ…Ð½Ñ Ð±Ð°Ð½Ð¸</h1>
          <p className="text-gray-600 mt-2">Ð£Ð¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸ÑÐ¼Ð¸ Ð¸ Ñ‚Ð¾Ð²Ð°Ñ€Ð°Ð¼Ð¸ Ð¼ÐµÐ½ÑŽ</p>
        </div>

        {/* ======================== */}
        {/* ðŸ“‚ Ð¡Ð•ÐšÐ¦Ð˜Ð¯: ÐšÐÐ¢Ð•Ð“ÐžÐ Ð˜Ð˜ */}
        {/* ======================== */}

        <div className="mb-12">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-semibold text-gray-800">ÐšÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸</h2>
            <button
              onClick={handleAddCategoryClick}
              className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl font-medium shadow-md transition flex items-center space-x-2"
            >
              <span>âž•</span>
              <span>Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸ÑŽ</span>
            </button>
          </div>

          {/* Ð¤Ð¾Ñ€Ð¼Ð° Ð´Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ñ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸ */}
          {isAddingNewCategory && (
            <div className="bg-white rounded-2xl shadow-lg p-6 mb-8 border border-gray-100">
              <h3 className="text-xl font-semibold text-gray-800 mb-4">Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð½Ð¾Ð²ÑƒÑŽ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸ÑŽ</h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                <input
                  type="text"
                  placeholder="Slug (Ð»Ð°Ñ‚Ð¸Ð½Ð¸Ñ†Ð°, Ð½Ð°Ð¿Ñ€. drinks)"
                  value={newCategory.slug}
                  onChange={(e) => setNewCategory({ ...newCategory, slug: e.target.value })}
                  className="px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                  autoFocus
                />
                <input
                  type="text"
                  placeholder="ÐÐ°Ð·Ð²Ð°Ð½Ð¸Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸"
                  value={newCategory.name}
                  onChange={(e) => setNewCategory({ ...newCategory, name: e.target.value })}
                  className="px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                />
                <input
                  type="number"
                  placeholder="ÐŸÐ¾Ñ€ÑÐ´Ð¾Ðº ÑÐ¾Ñ€Ñ‚Ð¸Ñ€Ð¾Ð²ÐºÐ¸"
                  value={newCategory.order}
                  onChange={(e) => setNewCategory({ ...newCategory, order: e.target.value })}
                  className="px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                />
              </div>
              <div className="flex space-x-4">
                <button
                  onClick={handleAddCategory}
                  className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl font-medium shadow transition"
                >
                  Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ
                </button>
                <button
                  onClick={handleCancelCategoryAdd}
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 px-6 py-3 rounded-xl font-medium shadow transition"
                >
                  ÐžÑ‚Ð¼ÐµÐ½Ð°
                </button>
              </div>
            </div>
          )}

          {/* Ð¢Ð°Ð±Ð»Ð¸Ñ†Ð° ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¹ */}
          <div className="bg-white rounded-2xl shadow-lg overflow-hidden">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">ID</th>
                  <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">Slug</th>
                  <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">ÐÐ°Ð·Ð²Ð°Ð½Ð¸Ðµ</th>
                  <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">ÐŸÐ¾Ñ€ÑÐ´Ð¾Ðº</th>
                  <th className="px-6 py-4 text-right text-sm font-medium text-gray-700 uppercase tracking-wider">Ð”ÐµÐ¹ÑÑ‚Ð²Ð¸Ñ</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {categories.length > 0 ? (
                  categories.map((category) => (
                    <tr key={category.id} className="hover:bg-gray-50">
                      {editingCategoryId === category.id ? (
                        <>
                          <td className="px-6 py-4">{category.id}</td>
                          <td className="px-6 py-4">
                            <input
                              type="text"
                              value={editCategoryForm.slug}
                              onChange={(e) => setEditCategoryForm({ ...editCategoryForm, slug: e.target.value })}
                              className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                            />
                          </td>
                          <td className="px-6 py-4">
                            <input
                              type="text"
                              value={editCategoryForm.name}
                              onChange={(e) => setEditCategoryForm({ ...editCategoryForm, name: e.target.value })}
                              className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                            />
                          </td>
                          <td className="px-6 py-4">
                            <input
                              type="number"
                              value={editCategoryForm.order}
                              onChange={(e) => setEditCategoryForm({ ...editCategoryForm, order: e.target.value })}
                              className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                            />
                          </td>
                          <td className="px-6 py-4 text-right">
                            <div className="flex justify-end space-x-2">
                              <button
                                onClick={handleSaveCategoryEdit}
                                className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg text-sm font-medium shadow transition"
                              >
                                Ð¡Ð¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ
                              </button>
                              <button
                                onClick={handleCancelCategoryEdit}
                                className="bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded-lg text-sm font-medium shadow transition"
                              >
                                ÐžÑ‚Ð¼ÐµÐ½Ð°
                              </button>
                            </div>
                          </td>
                        </>
                      ) : (
                        <>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{category.id}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{category.slug}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{category.name}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{category.order}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                            <button
                              onClick={() => handleEditCategoryClick(category)}
                              className="text-green-600 hover:text-green-900 mr-4 transition"
                            >
                              Ð ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ
                            </button>
                            <button
                              onClick={() => handleDeleteCategoryClick(category.id)}
                              className="text-red-600 hover:text-red-900 transition"
                            >
                              Ð£Ð´Ð°Ð»Ð¸Ñ‚ÑŒ
                            </button>
                          </td>
                        </>
                      )}
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" className="px-6 py-12 text-center text-gray-500 text-lg">
                      ÐšÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸ Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½Ñ‹. Ð”Ð¾Ð±Ð°Ð²ÑŒÑ‚Ðµ Ð¿ÐµÑ€Ð²ÑƒÑŽ!
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* ======================== */}
        {/* ðŸ½ï¸ Ð¡Ð•ÐšÐ¦Ð˜Ð¯: Ð¢ÐžÐ’ÐÐ Ð« */}
        {/* ======================== */}

        <div>
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-semibold text-gray-800">Ð¢Ð¾Ð²Ð°Ñ€Ñ‹</h2>
            <button
              onClick={handleAddItemClick}
              className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl font-medium shadow-md transition flex items-center space-x-2"
            >
              <span>âž•</span>
              <span>Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ñ‚Ð¾Ð²Ð°Ñ€</span>
            </button>
          </div>

          {/* Ð¤Ð¾Ñ€Ð¼Ð° Ð´Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ñ Ñ‚Ð¾Ð²Ð°Ñ€Ð° */}
          {isAddingNewItem && (
            <div className="bg-white rounded-2xl shadow-lg p-6 mb-8 border border-gray-100">
              <h3 className="text-xl font-semibold text-gray-800 mb-4">Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð½Ð¾Ð²Ñ‹Ð¹ Ñ‚Ð¾Ð²Ð°Ñ€</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
                <input
                  type="text"
                  placeholder="ÐÐ°Ð·Ð²Ð°Ð½Ð¸Ðµ Ñ‚Ð¾Ð²Ð°Ñ€Ð°"
                  value={newItem.name}
                  onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
                  className="px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                  autoFocus
                />
                <input
                  type="number"
                  placeholder="Ð¦ÐµÐ½Ð° (â‚½)"
                  value={newItem.price}
                  onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
                  className="px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                />
                <select
                  value={newItem.category_id}
                  onChange={(e) => setNewItem({ ...newItem, category_id: e.target.value })}
                  className="px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 w-full"
                >
                  <option value="">Ð’Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸ÑŽ</option>
                  {categories.map(cat => (
                    <option key={cat.id} value={cat.id}>{cat.name}</option>
                  ))}
                </select>
              </div>
              <div className="mb-6">
                <textarea
                  placeholder="ÐžÐ¿Ð¸ÑÐ°Ð½Ð¸Ðµ Ñ‚Ð¾Ð²Ð°Ñ€Ð°"
                  value={newItem.description}
                  onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
                  className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500"
                  rows="3"
                ></textarea>
              </div>
              <div className="flex space-x-4">
                <button
                  onClick={handleAddItem}
                  className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl font-medium shadow transition"
                >
                  Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ
                </button>
                <button
                  onClick={handleCancelItemAdd}
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 px-6 py-3 rounded-xl font-medium shadow transition"
                >
                  ÐžÑ‚Ð¼ÐµÐ½Ð°
                </button>
              </div>
            </div>
          )}

          {/* Ð¢Ð°Ð±Ð»Ð¸Ñ†Ð° Ñ‚Ð¾Ð²Ð°Ñ€Ð¾Ð² */}
          <div className="bg-white rounded-2xl shadow-lg overflow-hidden">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">ÐÐ°Ð·Ð²Ð°Ð½Ð¸Ðµ</th>
                  <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">Ð¦ÐµÐ½Ð°</th>
                  <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">ÐšÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ñ</th>
                  <th className="px-6 py-4 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">ÐžÐ¿Ð¸ÑÐ°Ð½Ð¸Ðµ</th>
                  <th className="px-6 py-4 text-right text-sm font-medium text-gray-700 uppercase tracking-wider">Ð”ÐµÐ¹ÑÑ‚Ð²Ð¸Ñ</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {items.length > 0 ? (
                  items.map((item) => (
                    <tr key={item.id} className="hover:bg-gray-50">
                      {editingItemId === item.id ? (
                        <>
                          <td className="px-6 py-4">
                            <input
                              type="text"
